import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SupplierList.css';

const SupplierList = () => {
  const navigate = useNavigate();
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [showModal, setShowModal] = useState(false);
  const [editing, setEditing] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [modalError, setModalError] = useState('');

  const [form, setForm] = useState({
    suppCode: '',
    suppName: '',
    address: '',
    district: '',
    city: '',
    pin: '',
    phone: '',
    email: '',
    gstNumber: '',
    pan: '',
    status: true,
  });

  // Validation helpers
  const validatePin = (pin) => /^[1-9][0-9]{5}$/.test(pin);
  const validatePhone = (phone) => /^[6-9]\d{9}$/.test(phone);
  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // Duplicate checks
  const isDuplicateSuppCode = (code) => {
    return suppliers.some(s => s.suppCode?.toLowerCase() === code.toLowerCase() && (!editing || s.id !== editing.id));
  };
  const isDuplicateSuppName = (name) => {
    return suppliers.some(s => s.suppName?.toLowerCase() === name.toLowerCase() && (!editing || s.id !== editing.id));
  };

  const fetchSuppliers = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/suppliers');
      if (res.data?.success) {
        setSuppliers(res.data.suppliers || []);
        setError('');
      } else {
        setSuppliers([]);
        setError(res.data?.message || 'Failed to load suppliers');
      }
    } catch (err) {
      console.error('Error fetching suppliers:', err);
      setSuppliers([]);
      setError('Failed to fetch suppliers.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchSuppliers(); }, []);

  const handleOpenModal = (supplier = null) => {
    setEditing(supplier);
    if (supplier) {
      setForm({
        suppCode: supplier.suppCode || '',
        suppName: supplier.suppName || '',
        address: supplier.address || '',
        district: supplier.district || '',
        city: supplier.city || '',
        pin: supplier.pin || '',
        phone: supplier.phone || '',
        email: supplier.email || '',
        gstNumber: supplier.gstNumber || '',
        pan: supplier.pan || '',
        status: supplier.status ?? true,
      });
    } else {
      setForm({
        suppCode: '', suppName: '', address: '', district: '', city: '', pin: '', phone: '', email: '', gstNumber: '', pan: '', status: true,
      });
    }
    setValidationErrors({});
    setModalError('');
    setError('');
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditing(null);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!validateForm()) {
        return;
      }
      if (editing) {
        const res = await axios.put(`/api/suppliers/${editing.id}`, form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          setModalError(res.data?.message || 'Failed to update supplier');
        }
      } else {
        const res = await axios.post('/api/suppliers', form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          setModalError(res.data?.message || 'Failed to create supplier');
        }
      }
    } catch (err) {
      console.error('Error saving supplier:', err);
      setModalError('Error saving supplier.');
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!form.suppCode) errors.suppCode = 'Supplier code is required';
    if (!form.suppName) errors.suppName = 'Supplier name is required';
    if (form.pin && !validatePin(form.pin)) errors.pin = 'Invalid PIN code';
    if (form.phone && !validatePhone(form.phone)) errors.phone = 'Invalid phone number';
    if (form.email && !validateEmail(form.email)) errors.email = 'Invalid email address';
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const isFormValidQuick = () => {
    if (!form.suppCode || !form.suppName) return false;
    if (form.pin && !validatePin(form.pin)) return false;
    if (form.phone && !validatePhone(form.phone)) return false;
    if (form.email && !validateEmail(form.email)) return false;
    return true;
  };

  return (
    <div className="supplier-list-container">
      <div className="supplier-list-header">
        <h1>Supplier List</h1>
        <div className="header-buttons">
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
          <button className="add-btn" onClick={() => handleOpenModal()}>Add Supplier</button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Loading suppliers...</div>
      ) : (
        <div className="table-container">
          <table className="supplier-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Supp Code</th>
                <th>Supp Name</th>
                <th>Address</th>
                <th>District</th>
                <th>City</th>
                <th>Pin</th>
                <th>Phone</th>
                <th>Email</th>
                <th>GSTNumber</th>
                <th>Pan</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {suppliers.length === 0 ? (
                <tr>
                  <td colSpan="13" className="no-data">No suppliers found</td>
                </tr>
              ) : (
                suppliers.map(s => (
                  <tr key={s.id}>
                    <td>{s.id}</td>
                    <td>{s.suppCode}</td>
                    <td>{s.suppName}</td>
                    <td>{s.address}</td>
                    <td>{s.district}</td>
                    <td>{s.city}</td>
                    <td>{s.pin}</td>
                    <td>{s.phone}</td>
                    <td>{s.email}</td>
                    <td>{s.gstNumber}</td>
                    <td>{s.pan}</td>
                    <td>{s.status ? 'Active' : 'Inactive'}</td>
                    <td className="actions">
                      <button className="edit-btn" onClick={() => handleOpenModal(s)}>Edit</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editing ? 'Edit Supplier' : 'Add Supplier'}</h2>
              <div className="header-actions">
                <button type="submit" className="save-btn" form="supplier-form" disabled={!isFormValidQuick()}>Save</button>
                <button className="close-btn" onClick={handleCloseModal}>Ã—</button>
              </div>
            </div>
            <form onSubmit={handleSubmit} className="supplier-form" id="supplier-form">
              {modalError && <div className="modal-error-message">{modalError}</div>}
              <div className="form-grid">
                <div className="form-group">
                  <label>Supp Code</label>
                  <input type="text" name="suppCode" value={form.suppCode} onChange={handleChange} className={validationErrors.suppCode ? 'error' : ''} />
                  {validationErrors.suppCode && <div className="error-message">{validationErrors.suppCode}</div>}
                </div>
                <div className="form-group">
                  <label>Supp Name</label>
                  <input type="text" name="suppName" value={form.suppName} onChange={handleChange} className={validationErrors.suppName ? 'error' : ''} />
                  {validationErrors.suppName && <div className="error-message">{validationErrors.suppName}</div>}
                </div>
                <div className="form-group span-2">
                  <label>Address</label>
                  <input type="text" name="address" value={form.address} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>District</label>
                  <input type="text" name="district" value={form.district} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>City</label>
                  <input type="text" name="city" value={form.city} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Pin</label>
                  <input type="text" name="pin" value={form.pin} onChange={handleChange} className={validationErrors.pin ? 'error' : ''} />
                  {validationErrors.pin && <div className="error-message">{validationErrors.pin}</div>}
                </div>
                <div className="form-group">
                  <label>Phone</label>
                  <input type="text" name="phone" value={form.phone} onChange={handleChange} className={validationErrors.phone ? 'error' : ''} />
                  {validationErrors.phone && <div className="error-message">{validationErrors.phone}</div>}
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input type="email" name="email" value={form.email} onChange={handleChange} className={validationErrors.email ? 'error' : ''} />
                  {validationErrors.email && <div className="error-message">{validationErrors.email}</div>}
                </div>
                <div className="form-group">
                  <label>GSTNumber</label>
                  <input type="text" name="gstNumber" value={form.gstNumber} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Pan</label>
                  <input type="text" name="pan" value={form.pan} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Status</label>
                  <input type="checkbox" name="status" checked={!!form.status} onChange={handleChange} /> Active
                </div>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default SupplierList;
