import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StoreList.css';

const SupplierList = () => {
  const navigate = useNavigate();
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [showModal, setShowModal] = useState(false);
  const [editing, setEditing] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [modalError, setModalError] = useState('');

  const [form, setForm] = useState({
    suppCode: '',
    suppName: '',
    address: '',
    district: '',
    city: '',
    pin: '',
    phone: '',
    email: '',
    gstNumber: '',
    pan: '',
    status: true,
  });

  // Validation helpers
  const validatePin = (pin) => /^[1-9][0-9]{5}$/.test(pin);
  const validatePhone = (phone) => /^[6-9]\d{9}$/.test(phone);
  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  // Duplicate checks
  const isDuplicateSuppCode = (code) => {
    return suppliers.some(s => s.suppCode?.toLowerCase() === code.toLowerCase() && (!editing || s.id !== editing.id));
  };
  const isDuplicateSuppName = (name) => {
    return suppliers.some(s => s.suppName?.toLowerCase() === name.toLowerCase() && (!editing || s.id !== editing.id));
  };

  const isFormValidQuick = () => {
    const hasRequired = form.suppCode.trim() !== '' && form.suppName.trim() !== '';
    const noDupes = !isDuplicateSuppCode(form.suppCode.trim()) && !isDuplicateSuppName(form.suppName.trim());
    return hasRequired && noDupes;
  };

  const validateForm = () => {
    const errors = {};
    if (!form.suppCode.trim()) {
      errors.suppCode = 'Supplier Code is required';
    } else if (isDuplicateSuppCode(form.suppCode.trim())) {
      errors.suppCode = 'Supplier Code already exists';
    }
    if (!form.suppName.trim()) {
      errors.suppName = 'Supplier Name is required';
    } else if (isDuplicateSuppName(form.suppName.trim())) {
      errors.suppName = 'Supplier Name already exists';
    }
    if (form.pin && form.pin.trim() && !validatePin(form.pin.trim())) {
      errors.pin = 'Pin must be a valid 6-digit postal code';
    }
    if (form.phone && form.phone.trim() && !validatePhone(form.phone.trim())) {
      errors.phone = 'Phone must be a valid 10-digit mobile number';
    }
    if (form.email && form.email.trim() && !validateEmail(form.email.trim())) {
      errors.email = 'Please enter a valid email address';
    }
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const fetchSuppliers = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/suppliers');
      if (res.data?.success) {
        setSuppliers(res.data.suppliers || []);
        setError('');
      } else {
        setSuppliers([]);
        setError(res.data?.message || 'Failed to load suppliers');
      }
    } catch (err) {
      console.error('Error fetching suppliers:', err);
      setSuppliers([]);
      setError('Failed to fetch suppliers.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchSuppliers(); }, []);

  const handleOpenModal = (supplier = null) => {
    setEditing(supplier);
    if (supplier) {
      setForm({
        suppCode: supplier.suppCode || '',
        suppName: supplier.suppName || '',
        address: supplier.address || '',
        district: supplier.district || '',
        city: supplier.city || '',
        pin: supplier.pin || '',
        phone: supplier.phone || '',
        email: supplier.email || '',
        gstNumber: supplier.gstNumber || '',
        pan: supplier.pan || '',
        status: supplier.status ?? true,
      });
    } else {
      setForm({
        suppCode: '', suppName: '', address: '', district: '', city: '', pin: '', phone: '', email: '', gstNumber: '', pan: '', status: true,
      });
    }
    setValidationErrors({});
    setModalError('');
    setError('');
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditing(null);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!validateForm()) {
        return;
      }
      if (editing) {
        const res = await axios.put(`/api/suppliers/${editing.id}`, form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          setModalError(res.data?.message || 'Failed to update supplier');
        }
      } else {
        const res = await axios.post('/api/suppliers', form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          setModalError(res.data?.message || 'Failed to create supplier');
        }
      }
    } catch (err) {
      console.error('Error saving supplier:', err);
      setModalError('Error saving supplier.');
    }
  };

  return (
    <div className="store-list-container">
      <div className="store-list-header">
        <h1>Supplier List</h1>
        <div className="header-buttons">
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
          <button className="add-btn" onClick={() => handleOpenModal()}>Add Supplier</button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Loading suppliers...</div>
      ) : (
        <div className="table-container">
          <table className="supplier-table">
             <thead>
               <tr>
                 <th>ID</th>
                 <th>Supp_Code</th>
                 <th>Supp_Name</th>
                 <th>Address</th>
                 <th>District</th>
                 <th>City</th>
                 <th>Pin</th>
                 <th>Phone</th>
                 <th>Email</th>
                 <th>GSTNumber</th>
                 <th>Pan</th>
                 <th>Actions</th>
               </tr>
             </thead>
             <tbody>
               {suppliers.length === 0 ? (
                 <tr>
                   <td colSpan="12" className="no-data">No suppliers found</td>
                 </tr>
               ) : (
                 suppliers.map(s => (
                   <tr key={s.id}>
                     <td>{s.id}</td>
                     <td>{s.suppCode}</td>
                     <td>{s.suppName}</td>
                     <td>{s.address}</td>
                     <td>{s.district}</td>
                     <td>{s.city}</td>
                     <td>{s.pin}</td>
                     <td>{s.phone}</td>
                     <td>{s.email}</td>
                     <td>{s.gstNumber}</td>
                     <td>{s.pan}</td>
                     <td className="actions">
                       <button className="edit-btn" onClick={() => handleOpenModal(s)}>Edit</button>
                     </td>
                   </tr>
                 ))
               )}
             </tbody>
           </table>
         </div>
       )}

       {showModal && (
         <div className="modal-overlay">
           <div className="modal">
             <div className="modal-header">
               <h2>{editing ? 'Edit Supplier' : 'Add Supplier'}</h2>
               <div className="header-actions">
                 <button type="submit" className="save-btn" form="supplier-form" disabled={!isFormValidQuick()}>Save</button>
                 <button className="close-btn" onClick={handleCloseModal}>Ã—</button>
               </div>
             </div>
             <form onSubmit={handleSubmit} className="store-form" id="store-form">
               {modalError && <div className="modal-error-message">{modalError}</div>}
               <div className="form-grid">
                 <div className="form-group">
                   <label>Supp_Code</label>
                   <input name="suppCode" value={form.suppCode} onChange={handleChange} className={validationErrors.suppCode ? 'error' : ''} required />
                   {validationErrors.suppCode && <span className="error-message">{validationErrors.suppCode}</span>}
                 </div>
                 <div className="form-group">
                   <label>Supp_Name</label>
                   <input name="suppName" value={form.suppName} onChange={handleChange} className={validationErrors.suppName ? 'error' : ''} required />
                   {validationErrors.suppName && <span className="error-message">{validationErrors.suppName}</span>}
                 </div>
                 <div className="form-group">
                   <label>Address</label>
                   <textarea name="address" value={form.address} onChange={handleChange} rows="3" />
                 </div>
                 <div className="form-group">
                   <label>District</label>
                   <input name="district" value={form.district} onChange={handleChange} />
                 </div>
                 <div className="form-group">
                   <label>City</label>
                   <input name="city" value={form.city} onChange={handleChange} />
                 </div>
                 <div className="form-group">
                   <label>Pin</label>
                   <input name="pin" value={form.pin} onChange={handleChange} className={validationErrors.pin ? 'error' : ''} placeholder="e.g., 110001" />
                   {validationErrors.pin && <span className="error-message">{validationErrors.pin}</span>}
                 </div>
                 <div className="form-group">
                   <label>Phone</label>
                   <input name="phone" value={form.phone} onChange={handleChange} className={validationErrors.phone ? 'error' : ''} placeholder="e.g., 9876543210" />
                   {validationErrors.phone && <span className="error-message">{validationErrors.phone}</span>}
                 </div>
                 <div className="form-group">
                   <label>Email</label>
                   <input type="email" name="email" value={form.email} onChange={handleChange} className={validationErrors.email ? 'error' : ''} placeholder="e.g., supplier@example.com" />
                   {validationErrors.email && <span className="error-message">{validationErrors.email}</span>}
                 </div>
                 <div className="form-group">
                   <label>GSTNumber</label>
                   <input name="gstNumber" value={form.gstNumber} onChange={handleChange} />
                 </div>
                 <div className="form-group">
                   <label>Pan</label>
                   <input name="pan" value={form.pan} onChange={handleChange} />
                 </div>
                 <div className="form-group">
                   <label>Status</label>
                   <input type="checkbox" name="status" checked={!!form.status} onChange={handleChange} /> Active
                 </div>
               </div>
             </form>
           </div>
         </div>
       )}
    </div>
  );
};

export default SupplierList;
