import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import { ScanBarcode, Trash2, Save, X, Store, Calendar, FileText, User } from 'lucide-react';

const SalesEntry = () => {
    // --- State ---
    // Header
    const [parties, setParties] = useState([]);
    const [selectedParty, setSelectedParty] = useState('');
    const [invoiceDate, setInvoiceDate] = useState(new Date().toISOString().split('T')[0]);
    const [invoiceNo, setInvoiceNo] = useState('New');
    const [storeInfo, setStoreInfo] = useState(null);

    // Item Input
    const [itemCode, setItemCode] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [currentItem, setCurrentItem] = useState(null); // { itemCode, itemName, mrp }
    const [itemError, setItemError] = useState('');
    const [allItems, setAllItems] = useState([]);

    // Body
    const [items, setItems] = useState([]); // Array of { itemCode, mrp, quantity, amount }

    // Footer
    const [tenderType, setTenderType] = useState('Cash');
    const [totalAmount, setTotalAmount] = useState(0);

    // Refs for focus management
    const quantityInputRef = useRef(null);
    const itemCodeInputRef = useRef(null);

    // --- Helpers ---
    const calculateTotal = useCallback(() => {
        const total = items.reduce((sum, item) => sum + item.amount, 0);
        setTotalAmount(total);
    }, [items]);

    // --- Effects ---
    useEffect(() => {
        fetchParties();
        fetchNextInvoiceNo();
        fetchStoreInfo();
        fetchAllItems();
    }, []);

    useEffect(() => {
        calculateTotal();
    }, [calculateTotal]);

    // --- API Calls ---
    const fetchAllItems = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/items');
            setAllItems(response.data);
        } catch (error) {
            console.error("Error fetching items", error);
        }
    };

    const fetchParties = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/parties');
            setParties(response.data);
        } catch (error) {
            console.error("Error fetching parties", error);
        }
    };

    const fetchNextInvoiceNo = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/generate-invoice-no');
            setInvoiceNo(response.data);
        } catch (error) {
            console.error("Error fetching invoice no", error);
        }
    };

    const fetchStoreInfo = async () => {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (user.userName) {
            try {
                const response = await axios.get(`http://localhost:8080/api/stores/by-user/${user.userName}`);
                if (response.data.success && response.data.stores && response.data.stores.length > 0) {
                    setStoreInfo(response.data.stores[0]);
                }
            } catch (error) {
                console.error("Error fetching store info", error);
            }
        }
    };

    const fetchItem = (code) => {
        if (!code) return;
        setItemError('');

        const foundItem = allItems.find(item => item.itemCode === code);

        if (foundItem) {
            setCurrentItem(foundItem);
            // Auto focus quantity after successful scan
            if (quantityInputRef.current) {
                quantityInputRef.current.focus();
                quantityInputRef.current.select();
            }
        } else {
            setCurrentItem(null);
            setItemError('Item not found');
        }
    };

    // --- Handlers ---
    const handleItemCodeKeyDown = (e) => {
        if (e.key === 'Enter') {
            fetchItem(itemCode);
        }
    };

    const handleQuantityKeyDown = (e) => {
        if (e.key === 'Enter') {
            addItemToBody();
        }
    };

    const addItemToBody = () => {
        if (!currentItem) {
            setItemError('Please scan a valid item first');
            return;
        }
        if (quantity <= 0) {
            setItemError('Quantity must be greater than 0');
            return;
        }

        const newItem = {
            itemCode: currentItem.itemCode,
            itemName: currentItem.itemName, // storing name for display if needed
            mrp: currentItem.mrp,
            quantity: parseInt(quantity),
            amount: currentItem.mrp * parseInt(quantity)
        };

        setItems(prevItems => {
            const existingItemIndex = prevItems.findIndex(item => item.itemCode === newItem.itemCode);
            if (existingItemIndex >= 0) {
                // Update existing
                const updatedItems = [...prevItems];
                const existingItem = updatedItems[existingItemIndex];
                const newQty = existingItem.quantity + newItem.quantity;
                updatedItems[existingItemIndex] = {
                    ...existingItem,
                    quantity: newQty,
                    amount: newQty * existingItem.mrp
                };
                return updatedItems;
            } else {
                // Add new
                return [...prevItems, newItem];
            }
        });

        // Reset inputs
        setItemCode('');
        setQuantity(1);
        setCurrentItem(null);
        setItemError('');
        // Focus back to item code for next scan
        if (itemCodeInputRef.current) {
            itemCodeInputRef.current.focus();
        }
    };

    const handleEditItem = (index, newQty) => {
        const qty = parseInt(newQty);
        if (isNaN(qty) || qty < 0) return;

        setItems(prevItems => {
            const updatedItems = [...prevItems];
            updatedItems[index] = {
                ...updatedItems[index],
                quantity: qty,
                amount: qty * updatedItems[index].mrp
            };
            return updatedItems;
        });
    };

    const handleDeleteItem = (index) => {
        setItems(prevItems => prevItems.filter((_, i) => i !== index));
    };

    const handleSave = async () => {
        // Validation
        if (!selectedParty) {
            alert('Please select a Party Name');
            return;
        }
        if (items.length === 0) {
            alert('Please add at least one item');
            return;
        }
        if (!tenderType) {
            alert('Please select a Tender Type');
            return;
        }

        if (!storeInfo || !storeInfo.storeCode) {
            alert('Store information missing. Cannot save.');
            return;
        }

        const payload = {
            invoiceNo,
            invoiceDate,
            partyCode: selectedParty,
            totalAmount,
            tenderType,
            storeCode: storeInfo.storeCode,
            items: items.map(item => ({
                itemCode: item.itemCode,
                mrp: item.mrp,
                quantity: item.quantity,
                amount: item.amount
            }))
        };

        try {
            await axios.post('http://localhost:8080/api/sales/save', payload);
            alert('Invoice saved successfully');
            resetForm();
        } catch (error) {
            console.error("Error saving invoice", error);
            alert('Failed to save invoice');
        }
    };

    const resetForm = () => {
        setItems([]);
        setSelectedParty('');
        setTenderType('Cash');
        setInvoiceDate(new Date().toISOString().split('T')[0]);
        setItemCode('');
        setQuantity(1);
        setCurrentItem(null);
        fetchNextInvoiceNo();
    };

    const handleCancel = () => {
        if (window.confirm('Do you want to cancel this invoice?')) {
            resetForm();
        }
    };

    return (
        <div className="min-h-screen bg-slate-50 p-2 flex flex-col items-center justify-center font-sans">
            <div className="w-full max-w-7xl bg-white rounded-xl shadow-sm overflow-hidden flex flex-col h-[95vh]">

                {/* Top Bar - Compact */}
                <div className="bg-white px-4 py-2 border-b border-slate-100 flex items-center justify-between relative shrink-0">
                    <div className="flex items-center gap-3">
                        <button 
                            onClick={() => navigate('/store-dashboard')}
                            className="p-1.5 hover:bg-slate-100 rounded-full text-slate-500 transition-colors"
                            title="Back to Dashboard"
                        >
                            <ArrowLeft className="w-5 h-5" />
                        </button>
                        <h2 className="text-lg font-bold text-slate-800 flex items-center gap-2">
                            Sales Voucher
                        </h2>
                    </div>

                    {storeInfo && (
                        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 flex items-center gap-2 text-xs font-medium text-slate-500 bg-slate-50 px-3 py-1 rounded-full border border-slate-100">
                            <Store className="w-3 h-3" />
                            <span>{storeInfo.storeCode} - {storeInfo.storeName}</span>
                        </div>
                    )}
                </div>

                {/* Header Fields (Row 1) - Compact */}
                <div className="p-3 border-b border-slate-100 bg-white grid grid-cols-1 md:grid-cols-3 gap-3 shrink-0">
                    {/* Party Name */}
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <User className="w-3 h-3" /> Party Name
                        </label>
                        <select 
                            className="w-full bg-slate-100 border-transparent focus:bg-white focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1.5 text-sm text-slate-700 transition-all outline-none h-9"
                            value={selectedParty}
                            onChange={(e) => setSelectedParty(e.target.value)}
                        >
                            <option value="">Select Party</option>
                            {parties.map(party => (
                                <option key={party.id} value={party.partyCode}>
                                    {party.partyName}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Invoice Date */}
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <Calendar className="w-3 h-3" /> Invoice Date
                        </label>
                        <input 
                            type="date" 
                            className="w-full bg-slate-100 border-transparent focus:bg-white focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1.5 text-sm text-slate-700 transition-all outline-none h-9"
                            value={invoiceDate}
                            onChange={(e) => setInvoiceDate(e.target.value)}
                        />
                    </div>

                    {/* Invoice Number */}
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <FileText className="w-3 h-3" /> Invoice Number
                        </label>
                        <input 
                            type="text" 
                            className="w-full bg-slate-100 border-transparent rounded px-2 py-1.5 text-sm text-slate-500 font-medium cursor-not-allowed outline-none h-9"
                            value={invoiceNo}
                            readOnly
                        />
                    </div>
                </div>

                {/* Item Entry (Row 2) - Compact */}
                <div className="px-4 py-2 bg-slate-50/50 border-b border-slate-100 shrink-0">
                    <div className="flex flex-col md:flex-row gap-2 items-end">
                        {/* Item Code */}
                        <div className="flex-grow md:flex-[2] space-y-1">
                            <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider">Item Code</label>
                            <div className="relative">
                                <ScanBarcode className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
                                <input 
                                    ref={itemCodeInputRef}
                                    type="text" 
                                    className="w-full pl-9 pr-3 py-1.5 bg-white border border-slate-200 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-100 rounded text-sm text-slate-700 transition-all outline-none h-9"
                                    value={itemCode}
                                    onChange={(e) => setItemCode(e.target.value)}
                                    onKeyDown={handleItemCodeKeyDown}
                                    onBlur={() => fetchItem(itemCode)}
                                    placeholder="Scan or enter item code..."
                                />
                            </div>
                            {itemError && <span className="text-xs text-red-500 font-medium ml-1">{itemError}</span>}
                        </div>

                        {/* Quantity */}
                        <div className="flex-grow md:flex-1 space-y-1">
                            <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider">Quantity</label>
                            <input 
                                ref={quantityInputRef}
                                type="number" 
                                className="w-full px-3 py-1.5 bg-white border border-slate-200 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-100 rounded text-sm text-slate-700 transition-all outline-none h-9"
                                value={quantity}
                                onChange={(e) => setQuantity(e.target.value)}
                                onKeyDown={handleQuantityKeyDown}
                                min="1"
                            />
                        </div>

                        {/* MRP */}
                        <div className="flex-grow md:flex-1 space-y-1">
                            <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider">MRP</label>
                            <input 
                                type="text" 
                                className="w-full px-3 py-1.5 bg-slate-100 border-transparent rounded text-sm text-slate-500 font-medium cursor-not-allowed outline-none h-9"
                                value={currentItem ? currentItem.mrp.toFixed(2) : ''}
                                readOnly
                                placeholder="0.00"
                            />
                        </div>

                        {/* Add Button */}
                        <div className="flex-none">
                            <button 
                                className="h-9 px-4 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded shadow-sm shadow-indigo-200 transition-all flex items-center gap-2 active:scale-95 text-sm"
                                onClick={addItemToBody}
                            >
                                <Save className="w-3.5 h-3.5" /> Add
                            </button>
                        </div>
                    </div>
                </div>

                {/* Table Body - Flexible height */}
                <div className="flex-1 overflow-auto bg-white min-h-0">
                    <table className="w-full text-left border-collapse">
                        <thead className="bg-white sticky top-0 z-10 shadow-sm">
                            <tr>
                                <th className="px-4 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100">Item Code</th>
                                <th className="px-4 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100">MRP</th>
                                <th className="px-4 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 w-24">Qty</th>
                                <th className="px-4 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100">Amount</th>
                                <th className="px-4 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 text-right">Actions</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-slate-50">
                            {items.map((item, index) => (
                                <tr key={index} className="hover:bg-slate-50/80 transition-colors">
                                    <td className="px-4 py-2 text-sm text-slate-700 font-medium">{item.itemCode}</td>
                                    <td className="px-4 py-2 text-sm text-slate-600"> {item.mrp.toFixed(2)}</td>
                                    <td className="px-4 py-2">
                                        <input 
                                            type="number"
                                            className="w-16 px-2 py-1 text-sm bg-white border border-slate-200 rounded focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 outline-none transition-all"
                                            value={item.quantity}
                                            onChange={(e) => handleEditItem(index, e.target.value)}
                                            min="1"
                                        />
                                    </td>
                                    <td className="px-4 py-2 text-sm font-semibold text-slate-700"> {item.amount.toFixed(2)}</td>
                                    <td className="px-4 py-2 text-right">
                                        <button 
                                            className="text-red-500 hover:text-red-700 hover:bg-red-50 px-2 py-1 rounded text-xs font-medium transition-colors flex items-center gap-1 ml-auto"
                                            onClick={() => handleDeleteItem(index)}
                                        >
                                            <Trash2 className="w-3.5 h-3.5" /> Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                            {items.length === 0 && (
                                <tr>
                                    <td colSpan="5" className="px-6 py-8 text-center text-slate-400">
                                        <div className="flex flex-col items-center gap-1">
                                            <ScanBarcode className="w-8 h-8 text-slate-200" />
                                            <p className="text-sm font-medium">No items added yet</p>
                                        </div>
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>

                {/* Footer - Compact */}
                <div className="bg-white border-t border-slate-100 p-3 flex flex-col md:flex-row items-center justify-between gap-4 sticky bottom-0 z-20 shadow-[0_-4px_6px_-1px_rgba(0,0,0,0.05)] shrink-0">

                    {/* Left: Total & Tender */}
                    <div className="flex flex-col md:flex-row items-center gap-6 w-full md:w-auto">
                        <div className="flex flex-col">
                            <span className="text-[10px] font-semibold text-slate-500 uppercase tracking-wider mb-0.5">Total Amount</span>
                            <span className="text-2xl font-bold text-[#10B981]"> {totalAmount.toFixed(2)}</span>
                        </div>

                        <div className="flex flex-col w-full md:w-auto">
                            <span className="text-[10px] font-semibold text-slate-500 uppercase tracking-wider mb-1">Tender Type</span>
                            <div className="flex bg-slate-100 p-0.5 rounded-lg">
                                {['Cash', 'Card', 'Credit'].map((type) => (
                                    <button
                                        key={type}
                                        onClick={() => setTenderType(type)}
                                        className={`px-3 py-1 rounded-md text-xs font-medium transition-all ${
                                            tenderType === type 
                                            ? 'bg-white text-indigo-600 shadow-sm' 
                                            : 'text-slate-500 hover:text-slate-700'
                                        }`}
                                    >
                                        {type}
                                    </button>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Right: Actions */}
                    <div className="flex items-center gap-2 w-full md:w-auto">
                        <button 
                            className="flex-1 md:flex-none px-4 py-1.5 bg-slate-100 hover:bg-slate-200 text-slate-600 text-sm font-medium rounded transition-colors flex items-center justify-center gap-1"
                            onClick={handleCancel}
                        >
                            <X className="w-3.5 h-3.5" /> Cancel
                        </button>
                        <button 
                            className="flex-1 md:flex-none px-6 py-1.5 bg-indigo-600 hover:bg-indigo-700 text-white text-sm font-medium rounded shadow-sm shadow-indigo-200 hover:scale-105 transition-all flex items-center justify-center gap-1"
                            onClick={handleSave}
                        >
                            <Save className="w-3.5 h-3.5" /> Save
                        </button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default SalesEntry;
