import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>RGSons Dashboard</h1>
          <div className="user-info">
            <span>Welcome, {user.userName || 'User'}!</span>
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-content">
          <div className="welcome-card">
            <h2>Welcome to RGSons Store Management System</h2>
            <p>You have successfully logged in!</p>

            {user.role && (
              <div className="user-details">
                <p><strong>Role:</strong> {user.role}</p>
                <p><strong>Status:</strong> {user.status ? 'Active' : 'Inactive'}</p>
              </div>
            )}
          </div>

          <div className="features-grid">
            <div className="feature-card">
              <h3>Store Management</h3>
              <p>Manage store information and operations</p>
            </div>

            <div className="feature-card">
              <h3>User Management</h3>
              <p>Handle user accounts and permissions</p>
            </div>

            <div className="feature-card">
              <h3>Reports</h3>
              <p>View and generate business reports</p>
            </div>

            <div className="feature-card">
              <h3>Settings</h3>
              <p>Configure system settings</p>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
