import React, { useState } from 'react';
import './Transactions.css';

const Transactions = () => {
  // Header-level form
  const [date, setDate] = useState(() => new Date().toISOString().slice(0, 10));
  const [refNo, setRefNo] = useState('');
  const [supplier, setSupplier] = useState('');

  // Line editor state
  const [itemCode, setItemCode] = useState('');
  const [itemName, setItemName] = useState('');
  const [size, setSize] = useState('');
  const [brand, setBrand] = useState('');
  const [quantity, setQuantity] = useState('');
  const [purPrice, setPurPrice] = useState('');

  // Handle item code input and auto-focus next field
  const handleItemCodeChange = (e) => {
    setItemCode(e.target.value);
  };

  const handleItemCodeKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      // Focus on item name field
      document.getElementById('itemName').focus();
    }
  };

  const handleFieldKeyPress = (e, nextFieldId) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      if (nextFieldId) {
        document.getElementById(nextFieldId).focus();
      }
    }
  };

  const [lines, setLines] = useState([]);

  const addLine = () => {
    const qty = parseFloat(quantity) || 0;
    const price = parseFloat(purPrice) || 0;
    if (!itemCode || !itemName || qty <= 0 || price < 0) {
      alert('Please fill Item Code, Item Name, valid Quantity and Purchase Price');
      return;
    }
    const newLine = {
      id: Date.now(),
      itemCode,
      itemName,
      size,
      brand,
      quantity: qty,
      purPrice: price,
      total: +(qty * price).toFixed(2),
    };
    setLines((prev) => [...prev, newLine]);
    // reset editor for next entry
    setItemCode('');
    setItemName('');
    setSize('');
    setBrand('');
    setQuantity('');
    setPurPrice('');
    // Focus back to item code for next entry
    setTimeout(() => {
      document.getElementById('itemCode').focus();
    }, 100);
  };

  const removeLine = (id) => {
    setLines((prev) => prev.filter((l) => l.id !== id));
  };

  const grandTotal = lines.reduce((sum, l) => sum + l.total, 0).toFixed(2);

  const handleReset = () => {
    setDate(new Date().toISOString().slice(0, 10));
    setRefNo('');
    setSupplier('');
    setLines([]);
    setItemCode('');
    setItemName('');
    setSize('');
    setBrand('');
    setQuantity('');
    setPurPrice('');
  };

  const handleSaveDraft = () => {
    console.log('Draft saved (local only):', { date, refNo, supplier, lines });
    alert('Draft saved locally (no backend calls).');
  };

  const handleSubmit = () => {
    if (lines.length === 0) {
      alert('Please add at least one line');
      return;
    }
    console.log('Submitting Stock In (no backend):', { date, refNo, supplier, lines });
    alert('Stock In submitted (UI only, no backend).');
  };

  return (
    <div className="transactions-container">
      <header className="transactions-header">
        <h1>Transactions</h1>
        <p className="subtitle">Stock In</p>
      </header>

      <div className="stockin-form">
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="date">Date</label>
            <input id="date" type="date" value={date} onChange={(e) => setDate(e.target.value)} />
          </div>
          <div className="form-group">
            <label htmlFor="refNo">Reference / Invoice No</label>
            <input id="refNo" type="text" placeholder="INV-001" value={refNo} onChange={(e) => setRefNo(e.target.value)} />
          </div>
          <div className="form-group">
            <label htmlFor="supplier">Supplier</label>
            <input id="supplier" type="text" placeholder="Supplier Name" value={supplier} onChange={(e) => setSupplier(e.target.value)} />
          </div>
        </div>

        <h2 className="section-title">Add Items</h2>
        <div className="line-editor">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="itemCode">Item Code</label>
              <input 
                id="itemCode" 
                type="text" 
                value={itemCode} 
                onChange={handleItemCodeChange}
                onKeyPress={handleItemCodeKeyPress}
                placeholder="e.g., ITM-1001" 
                autoFocus
              />
            </div>
            <div className="form-group">
              <label htmlFor="itemName">Item Name</label>
              <input 
                id="itemName" 
                type="text" 
                value={itemName} 
                onChange={(e) => setItemName(e.target.value)} 
                onKeyPress={(e) => handleFieldKeyPress(e, 'size')}
                placeholder="e.g., T-Shirt" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="size">Size</label>
              <input 
                id="size" 
                type="text" 
                value={size} 
                onChange={(e) => setSize(e.target.value)} 
                onKeyPress={(e) => handleFieldKeyPress(e, 'brand')}
                placeholder="e.g., M" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="brand">Brand</label>
              <input 
                id="brand" 
                type="text" 
                value={brand} 
                onChange={(e) => setBrand(e.target.value)} 
                onKeyPress={(e) => handleFieldKeyPress(e, 'quantity')}
                placeholder="e.g., RG" 
              />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="quantity">Quantity</label>
              <input 
                id="quantity" 
                type="number" 
                min="0" 
                step="1" 
                value={quantity} 
                onChange={(e) => setQuantity(e.target.value)} 
                onKeyPress={(e) => handleFieldKeyPress(e, 'purPrice')}
                placeholder="0" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="purPrice">Purchase Price</label>
              <input 
                id="purPrice" 
                type="number" 
                min="0" 
                step="0.01" 
                value={purPrice} 
                onChange={(e) => setPurPrice(e.target.value)} 
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    addLine();
                  }
                }}
                placeholder="0.00" 
              />
            </div>
            <div className="form-group total-display">
              <label>Line Total</label>
              <div className="total-box">
                {(() => {
                  const qty = parseFloat(quantity) || 0;
                  const price = parseFloat(purPrice) || 0;
                  return (qty * price).toFixed(2);
                })()}
              </div>
            </div>
            <div className="form-actions-inline">
              <button className="add-line-btn" type="button" onClick={addLine}>Add Line</button>
            </div>
          </div>
        </div>

        <h2 className="section-title">Stock In Lines</h2>
        <div className="lines-table-wrapper">
          {lines.length === 0 ? (
            <div className="no-lines">No items added yet.</div>
          ) : (
            <table className="lines-table">
              <thead>
                <tr>
                  <th>Item Code</th>
                  <th>Item Name</th>
                  <th>Size</th>
                  <th>Brand</th>
                  <th>Quantity</th>
                  <th>Purchase Price</th>
                  <th>Total</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {lines.map((l) => (
                  <tr key={l.id}>
                    <td>{l.itemCode}</td>
                    <td>{l.itemName}</td>
                    <td>{l.size}</td>
                    <td>{l.brand}</td>
                    <td>{l.quantity}</td>
                    <td>{l.purPrice.toFixed(2)}</td>
                    <td>{l.total.toFixed(2)}</td>
                    <td>
                      <button className="delete-line-btn" type="button" onClick={() => removeLine(l.id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        <div className="summary-row">
          <div className="summary-box">
            <span className="label">Grand Total:</span>
            <span className="value">â‚¹ {grandTotal}</span>
          </div>
        </div>

        <div className="form-actions">
          <button className="reset-btn" type="button" onClick={handleReset}>Reset</button>
          <button className="save-btn" type="button" onClick={handleSaveDraft}>Save Draft</button>
          <button className="submit-btn" type="button" onClick={handleSubmit}>Submit</button>
        </div>
      </div>
    </div>
  );
};

export default Transactions;
