import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const SupplierList = () => {
  const navigate = useNavigate();
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [showModal, setShowModal] = useState(false);
  const [editing, setEditing] = useState(null);

  const [form, setForm] = useState({
    suppCode: '',
    suppName: '',
    address: '',
    district: '',
    city: '',
    pin: '',
    phone: '',
    email: '',
    gstNumber: '',
    pan: '',
    status: true,
  });

  const fetchSuppliers = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/suppliers');
      if (res.data?.success) {
        setSuppliers(res.data.suppliers || []);
        setError('');
      } else {
        setSuppliers([]);
        setError(res.data?.message || 'Failed to load suppliers');
      }
    } catch (err) {
      console.error('Error fetching suppliers:', err);
      setSuppliers([]);
      setError('Failed to fetch suppliers.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchSuppliers(); }, []);

  const handleOpenModal = (supplier = null) => {
    setEditing(supplier);
    if (supplier) {
      setForm({
        suppCode: supplier.suppCode || '',
        suppName: supplier.suppName || '',
        address: supplier.address || '',
        district: supplier.district || '',
        city: supplier.city || '',
        pin: supplier.pin || '',
        phone: supplier.phone || '',
        email: supplier.email || '',
        gstNumber: supplier.gstNumber || '',
        pan: supplier.pan || '',
        status: supplier.status ?? true,
      });
    } else {
      setForm({
        suppCode: '', suppName: '', address: '', district: '', city: '', pin: '', phone: '', email: '', gstNumber: '', pan: '', status: true,
      });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditing(null);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!form.suppCode || !form.suppName) {
        alert('Supplier Code and Supplier Name are required');
        return;
      }
      if (editing) {
        const res = await axios.put(`/api/suppliers/${editing.id}`, form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          alert(res.data?.message || 'Failed to update supplier');
        }
      } else {
        const res = await axios.post('/api/suppliers', form);
        if (res.data?.success) {
          handleCloseModal();
          fetchSuppliers();
        } else {
          alert(res.data?.message || 'Failed to create supplier');
        }
      }
    } catch (err) {
      console.error('Error saving supplier:', err);
      alert('Error saving supplier.');
    }
  };

  return (
    <div className="supplier-list-container" style={{ padding: '20px' }}>
      <div className="supplier-list-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1>Supplier Management</h1>
        <div className="header-buttons" style={{ display: 'flex', gap: '10px' }}>
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
          <button className="add-btn" onClick={() => handleOpenModal()}>Add Supplier</button>
        </div>
      </div>

      {error && <div className="error-message" style={{ color: 'red', marginTop: '10px' }}>{error}</div>}

      {loading ? (
        <div className="loading">Loading suppliers...</div>
      ) : (
        <div className="table-container" style={{ marginTop: '20px', overflowX: 'auto' }}>
          <table className="supplier-table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Supp_Code</th>
                <th>Supp_Name</th>
                <th>Address</th>
                <th>District</th>
                <th>City</th>
                <th>Pin</th>
                <th>Phone</th>
                <th>Email</th>
                <th>GSTNumber</th>
                <th>Pan</th>
                <th>Status</th>
                <th>CreatedAt</th>
                <th>UpdateAt</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {suppliers.length === 0 ? (
                <tr>
                  <td colSpan="15" className="no-data">No suppliers found</td>
                </tr>
              ) : (
                suppliers.map(s => (
                  <tr key={s.id}>
                    <td>{s.id}</td>
                    <td>{s.suppCode}</td>
                    <td>{s.suppName}</td>
                    <td>{s.address}</td>
                    <td>{s.district}</td>
                    <td>{s.city}</td>
                    <td>{s.pin}</td>
                    <td>{s.phone}</td>
                    <td>{s.email}</td>
                    <td>{s.gstNumber}</td>
                    <td>{s.pan}</td>
                    <td>{s.status ? 'Active' : 'Inactive'}</td>
                    <td>{s.createdAt ? new Date(s.createdAt).toLocaleString() : ''}</td>
                    <td>{s.updateAt ? new Date(s.updateAt).toLocaleString() : ''}</td>
                    <td>
                      <button onClick={() => handleOpenModal(s)}>Edit</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <div className="modal-overlay" style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.4)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div className="modal" style={{ background: '#fff', padding: '20px', borderRadius: '10px', width: '800px', maxWidth: '95%' }}>
            <h2>{editing ? 'Edit Supplier' : 'Add Supplier'}</h2>
            <form onSubmit={handleSubmit} className="supplier-form" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
              <div>
                <label>Supp_Code</label>
                <input name="suppCode" value={form.suppCode} onChange={handleChange} required />
              </div>
              <div>
                <label>Supp_Name</label>
                <input name="suppName" value={form.suppName} onChange={handleChange} required />
              </div>
              <div style={{ gridColumn: 'span 2' }}>
                <label>Address</label>
                <input name="address" value={form.address} onChange={handleChange} />
              </div>
              <div>
                <label>District</label>
                <input name="district" value={form.district} onChange={handleChange} />
              </div>
              <div>
                <label>City</label>
                <input name="city" value={form.city} onChange={handleChange} />
              </div>
              <div>
                <label>Pin</label>
                <input name="pin" value={form.pin} onChange={handleChange} />
              </div>
              <div>
                <label>Phone</label>
                <input name="phone" value={form.phone} onChange={handleChange} />
              </div>
              <div>
                <label>Email</label>
                <input type="email" name="email" value={form.email} onChange={handleChange} />
              </div>
              <div>
                <label>GSTNumber</label>
                <input name="gstNumber" value={form.gstNumber} onChange={handleChange} />
              </div>
              <div>
                <label>Pan</label>
                <input name="pan" value={form.pan} onChange={handleChange} />
              </div>
              <div>
                <label>Status</label>
                <input type="checkbox" name="status" checked={!!form.status} onChange={handleChange} /> Active
              </div>

              <div style={{ gridColumn: 'span 2', display: 'flex', justifyContent: 'flex-end', gap: '10px', marginTop: '10px' }}>
                <button type="button" onClick={handleCloseModal}>Cancel</button>
                <button type="submit">{editing ? 'Update' : 'Create'}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default SupplierList;
