import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SupplierList.css';

const SupplierList = () => {
  const navigate = useNavigate();
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingSupplier, setEditingSupplier] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    suppCode: '',
    suppName: '',
    address: '',
    district: '',
    city: '',
    pin: '',
    phone: '',
    email: '',
    gstNumber: '',
    pan: '',
    status: true
  });

  // Validation functions
  const validatePin = (pin) => {
    const pinRegex = /^[1-9][0-9]{5}$/; // 6 digits, first digit cannot be 0
    return pinRegex.test(pin);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^[6-9]\d{9}$/; // 10 digits starting with 6-9
    return phoneRegex.test(phone);
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  // Validate form fields
  const validateForm = () => {
    const errors = {};

    // Required field validations
    if (!formData.suppCode.trim()) {
      errors.suppCode = 'Supplier Code is required';
    } else if (isDuplicateSuppCode(formData.suppCode.trim())) {
      errors.suppCode = 'Supplier Code already exists. Please use a different code.';
    }

    if (!formData.suppName.trim()) {
      errors.suppName = 'Supplier Name is required';
    } else if (isDuplicateSuppName(formData.suppName.trim())) {
      errors.suppName = 'Supplier Name already exists. Please use a different name.';
    }

    // Pin validation (only validate if not empty)
    if (formData.pin && formData.pin.trim() && !validatePin(formData.pin.trim())) {
      errors.pin = 'Pin must be a valid 6-digit postal code';
    }

    // Phone validation (only validate if not empty)
    if (formData.phone && formData.phone.trim() && !validatePhone(formData.phone.trim())) {
      errors.phone = 'Phone must be a valid 10-digit Indian mobile number';
    }

    // Email validation (only validate if not empty)
    if (formData.email && formData.email.trim() && !validateEmail(formData.email.trim())) {
      errors.email = 'Please enter a valid email address';
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Check for duplicate supplier code
  const isDuplicateSuppCode = (suppCode) => {
    return suppliers.some(supplier => 
      supplier.suppCode.toLowerCase() === suppCode.toLowerCase() && 
      (!editingSupplier || supplier.id !== editingSupplier.id)
    );
  };

  // Check for duplicate supplier name
  const isDuplicateSuppName = (suppName) => {
    return suppliers.some(supplier => 
      supplier.suppName.toLowerCase() === suppName.toLowerCase() && 
      (!editingSupplier || supplier.id !== editingSupplier.id)
    );
  };

  // Check if all required fields are filled and no validation errors exist
  const isFormValid = () => {
    const hasRequiredFields = formData.suppCode.trim() !== '' && formData.suppName.trim() !== '';
    const hasNoDuplicates = !isDuplicateSuppCode(formData.suppCode.trim()) && !isDuplicateSuppName(formData.suppName.trim());
    return hasRequiredFields && hasNoDuplicates;
  };

  // Fetch suppliers from API
  const fetchSuppliers = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/suppliers');
      // Backend returns {success: true, suppliers: [...], message: "...", count: n}
      if (response.data.success && response.data.suppliers) {
        setSuppliers(response.data.suppliers);
      } else {
        setSuppliers([]);
        setError(response.data.message || 'No suppliers found.');
      }
      setError('');
    } catch (error) {
      console.error('Error fetching suppliers:', error);
      setError('Failed to fetch suppliers. Please try again.');
      setSuppliers([]); // Ensure suppliers is always an array
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSuppliers();
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear validation error for this field when user starts typing
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Open modal for adding new supplier
  const handleAddSupplier = () => {
    setEditingSupplier(null);
    setFormData({
      suppCode: '',
      suppName: '',
      address: '',
      district: '',
      city: '',
      pin: '',
      phone: '',
      email: '',
      gstNumber: '',
      pan: '',
      status: true
    });
    setValidationErrors({}); // Clear validation errors
    setModalError(''); // Clear modal errors
    setError(''); // Clear general errors
    setShowModal(true);
  };

  // Open modal for editing existing supplier
  const handleEditSupplier = (supplier) => {
    setEditingSupplier(supplier);
    setFormData({
      suppCode: supplier.suppCode || '',
      suppName: supplier.suppName || '',
      address: supplier.address || '',
      district: supplier.district || '',
      city: supplier.city || '',
      pin: supplier.pin || '',
      phone: supplier.phone || '',
      email: supplier.email || '',
      gstNumber: supplier.gstNumber || '',
      pan: supplier.pan || '',
      status: supplier.status !== undefined ? supplier.status : true
    });
    setValidationErrors({}); // Clear validation errors
    setModalError(''); // Clear modal errors
    setError(''); // Clear general errors
    setShowModal(true);
  };

  // Close modal
  const handleCloseModal = () => {
    setShowModal(false);
    setEditingSupplier(null);
  };

  // Save supplier (add or update)
  const handleSaveSupplier = async (e) => {
    e.preventDefault();

    // Validate form before submission
    if (!validateForm()) {
      // Don't set general error, validation errors are shown inline
      return;
    }

    try {
      if (editingSupplier) {
        // Update existing supplier
        await axios.put(`/api/suppliers/${editingSupplier.id}`, formData);
      } else {
        // Add new supplier
        await axios.post('/api/suppliers', formData);
      }
      setShowModal(false);
      setValidationErrors({}); // Clear validation errors
      setModalError(''); // Clear modal errors
      fetchSuppliers(); // Refresh the list
      setError('');
    } catch (error) {
      console.error('Error saving supplier:', error);
      setModalError('Failed to save supplier. Please try again.');
    }
  };

  // Delete supplier
  const handleDeleteSupplier = async (supplierId) => {
    if (window.confirm('Are you sure you want to delete this supplier?')) {
      try {
        await axios.delete(`/api/suppliers/${supplierId}`);
        fetchSuppliers(); // Refresh the list
        setError('');
      } catch (error) {
        console.error('Error deleting supplier:', error);
        setError('Failed to delete supplier. Please try again.');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading suppliers...</div>;
  }

  return (
    <div className="supplier-list-container">
      <div className="supplier-list-header">
        <h1>Supplier List</h1>
        <div className="header-buttons">
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>
            Dashboard
          </button>
          <button className="add-btn" onClick={handleAddSupplier}>
            Add New Supplier
          </button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="table-container">
        <table className="supplier-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Supp Code</th>
              <th>Supp Name</th>
              <th>Address</th>
              <th>District</th>
              <th>City</th>
              <th>Pin</th>
              <th>Phone</th>
              <th>Email</th>
              <th>GST Number</th>
              <th>Pan</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.length === 0 ? (
              <tr>
                <td colSpan="13" className="no-data">No suppliers found</td>
              </tr>
            ) : (
              suppliers.map((supplier) => (
                <tr key={supplier.id}>
                  <td>{supplier.id}</td>
                  <td>{supplier.suppCode}</td>
                  <td>{supplier.suppName}</td>
                  <td>{supplier.address}</td>
                  <td>{supplier.district}</td>
                  <td>{supplier.city}</td>
                  <td>{supplier.pin}</td>
                  <td>{supplier.phone}</td>
                  <td>{supplier.email}</td>
                  <td>{supplier.gstNumber}</td>
                  <td>{supplier.pan}</td>
                  <td>{supplier.status ? 'Active' : 'Inactive'}</td>
                  <td className="actions">
                    <button 
                      className="edit-btn" 
                      onClick={() => handleEditSupplier(supplier)}
                    >
                      Edit
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={() => handleDeleteSupplier(supplier.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editing ? 'Edit Supplier' : 'Add Supplier'}</h2>
              <div className="header-actions">
                <button type="submit" className="save-btn" form="supplier-form" disabled={!isFormValidQuick()}>Save</button>
                <button className="close-btn" onClick={handleCloseModal}>Ã—</button>
              </div>
            </div>
            <form onSubmit={handleSubmit} className="supplier-form" id="supplier-form">
              {modalError && <div className="modal-error-message">{modalError}</div>}
              <div className="form-grid">
                <div className="form-group">
                  <label>Supp Code</label>
                  <input type="text" name="suppCode" value={form.suppCode} onChange={handleChange} className={validationErrors.suppCode ? 'error' : ''} />
                  {validationErrors.suppCode && <div className="error-message">{validationErrors.suppCode}</div>}
                </div>
                <div className="form-group">
                  <label>Supp Name</label>
                  <input type="text" name="suppName" value={form.suppName} onChange={handleChange} className={validationErrors.suppName ? 'error' : ''} />
                  {validationErrors.suppName && <div className="error-message">{validationErrors.suppName}</div>}
                </div>
                <div className="form-group span-2">
                  <label>Address</label>
                  <input type="text" name="address" value={form.address} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>District</label>
                  <input type="text" name="district" value={form.district} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>City</label>
                  <input type="text" name="city" value={form.city} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Pin</label>
                  <input type="text" name="pin" value={form.pin} onChange={handleChange} className={validationErrors.pin ? 'error' : ''} />
                  {validationErrors.pin && <div className="error-message">{validationErrors.pin}</div>}
                </div>
                <div className="form-group">
                  <label>Phone</label>
                  <input type="text" name="phone" value={form.phone} onChange={handleChange} className={validationErrors.phone ? 'error' : ''} />
                  {validationErrors.phone && <div className="error-message">{validationErrors.phone}</div>}
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input type="email" name="email" value={form.email} onChange={handleChange} className={validationErrors.email ? 'error' : ''} />
                  {validationErrors.email && <div className="error-message">{validationErrors.email}</div>}
                </div>
                <div className="form-group">
                  <label>GSTNumber</label>
                  <input type="text" name="gstNumber" value={form.gstNumber} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Pan</label>
                  <input type="text" name="pan" value={form.pan} onChange={handleChange} />
                </div>
                <div className="form-group">
                  <label>Status</label>
                  <input type="checkbox" name="status" checked={!!form.status} onChange={handleChange} /> Active
                </div>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default SupplierList;
