import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StoreList.css';

const StoreList = () => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingStore, setEditingStore] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    storeCode: '',
    storeName: '',
    address: '',
    area: '',
    zone: '',
    district: '',
    city: '',
    pin: '',
    phone: '',
    email: '',
    gstNumber: ''
  });

  // Validation functions
  const validatePin = (pin) => {
    const pinRegex = /^[1-9][0-9]{5}$/; // 6 digits, first digit cannot be 0
    return pinRegex.test(pin);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^[6-9]\d{9}$/; // 10 digits starting with 6-9
    return phoneRegex.test(phone);
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  // Validate form fields
  const validateForm = () => {
    const errors = {};

    // Required field validations
    if (!formData.storeCode.trim()) {
      errors.storeCode = 'Store Code is required';
    }
    if (!formData.storeName.trim()) {
      errors.storeName = 'Store Name is required';
    }

    // Pin validation
    if (formData.pin && !validatePin(formData.pin)) {
      errors.pin = 'Pin must be a valid 6-digit postal code';
    }

    // Phone validation
    if (formData.phone && !validatePhone(formData.phone)) {
      errors.phone = 'Phone must be a valid 10-digit Indian mobile number';
    }

    // Email validation
    if (formData.email && !validateEmail(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Fetch stores from API
  const fetchStores = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/stores');
      // Backend returns {success: true, stores: [...], message: "...", count: n}
      if (response.data.success && response.data.stores) {
        setStores(response.data.stores);
      } else {
        setStores([]);
        setError(response.data.message || 'No stores found.');
      }
      setError('');
    } catch (error) {
      console.error('Error fetching stores:', error);
      setError('Failed to fetch stores. Please try again.');
      setStores([]); // Ensure stores is always an array
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStores();
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear validation error for this field when user starts typing
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Open modal for adding new store
  const handleAddStore = () => {
    setEditingStore(null);
    setFormData({
      storeCode: '',
      storeName: '',
      address: '',
      area: '',
      zone: '',
      district: '',
      city: '',
      pin: '',
      phone: '',
      email: '',
      gstNumber: ''
    });
    setValidationErrors({}); // Clear validation errors
    setModalError(''); // Clear modal errors
    setError(''); // Clear general errors
    setShowModal(true);
  };

  // Open modal for editing existing store
  const handleEditStore = (store) => {
    setEditingStore(store);
    setFormData({
      storeCode: store.storeCode || '',
      storeName: store.storeName || '',
      address: store.address || '',
      area: store.area || '',
      zone: store.zone || '',
      district: store.district || '',
      city: store.city || '',
      pin: store.pin || '',
      phone: store.phone || '',
      email: store.email || '',
      gstNumber: store.gstNumber || ''
    });
    setValidationErrors({}); // Clear validation errors
    setModalError(''); // Clear modal errors
    setError(''); // Clear general errors
    setShowModal(true);
  };

  // Save store (add or update)
  const handleSaveStore = async (e) => {
    e.preventDefault();

    // Validate form before submission
    if (!validateForm()) {
      // Don't set general error, validation errors are shown inline
      return;
    }

    try {
      if (editingStore) {
        // Update existing store
        await axios.put(`/api/stores/${editingStore.id}`, formData);
      } else {
        // Add new store
        await axios.post('/api/stores', formData);
      }
      setShowModal(false);
      setValidationErrors({}); // Clear validation errors
      setModalError(''); // Clear modal errors
      fetchStores(); // Refresh the list
      setError('');
    } catch (error) {
      console.error('Error saving store:', error);
      setModalError('Failed to save store. Please try again.');
    }
  };

  // Delete store
  const handleDeleteStore = async (storeId) => {
    if (window.confirm('Are you sure you want to delete this store?')) {
      try {
        await axios.delete(`/api/stores/${storeId}`);
        fetchStores(); // Refresh the list
        setError('');
      } catch (error) {
        console.error('Error deleting store:', error);
        setError('Failed to delete store. Please try again.');
      }
    }
  };

  // Close modal
  const handleCloseModal = () => {
    setShowModal(false);
    setEditingStore(null);
  };

  if (loading) {
    return <div className="loading">Loading stores...</div>;
  }

  return (
    <div className="store-list-container">
      <div className="store-list-header">
        <h1>Store List</h1>
        <button className="add-btn" onClick={handleAddStore}>
          Add New Store
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="table-container">
        <table className="store-table">
          <thead>
            <tr>
              <th>Store Code</th>
              <th>Store Name</th>
              <th>Address</th>
              <th>Area</th>
              <th>Zone</th>
              <th>District</th>
              <th>City</th>
              <th>Pin</th>
              <th>Phone</th>
              <th>Email</th>
              <th>GST Number</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {stores.length === 0 ? (
              <tr>
                <td colSpan="12" className="no-data">No stores found</td>
              </tr>
            ) : (
              stores.map((store) => (
                <tr key={store.id}>
                  <td>{store.storeCode}</td>
                  <td>{store.storeName}</td>
                  <td>{store.address}</td>
                  <td>{store.area}</td>
                  <td>{store.zone}</td>
                  <td>{store.district}</td>
                  <td>{store.city}</td>
                  <td>{store.pin}</td>
                  <td>{store.phone}</td>
                  <td>{store.email}</td>
                  <td>{store.gstNumber}</td>
                  <td className="actions">
                    <button 
                      className="edit-btn" 
                      onClick={() => handleEditStore(store)}
                    >
                      Edit
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={() => handleDeleteStore(store.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Modal for Add/Edit Store */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editingStore ? 'Edit Store' : 'Add New Store'}</h2>
              <button className="close-btn" onClick={handleCloseModal}>
                Ã—
              </button>
            </div>
            <form onSubmit={handleSaveStore} className="store-form">
              {modalError && <div className="modal-error-message">{modalError}</div>}
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="storeCode">Store Code *</label>
                  <input
                    type="text"
                    id="storeCode"
                    name="storeCode"
                    value={formData.storeCode}
                    onChange={handleInputChange}
                    className={validationErrors.storeCode ? 'error' : ''}
                    required
                  />
                  {validationErrors.storeCode && (
                    <span className="error-message">{validationErrors.storeCode}</span>
                  )}
                </div>
                <div className="form-group">
                  <label htmlFor="storeName">Store Name *</label>
                  <input
                    type="text"
                    id="storeName"
                    name="storeName"
                    value={formData.storeName}
                    onChange={handleInputChange}
                    className={validationErrors.storeName ? 'error' : ''}
                    required
                  />
                  {validationErrors.storeName && (
                    <span className="error-message">{validationErrors.storeName}</span>
                  )}
                </div>
                <div className="form-group">
                  <label htmlFor="address">Address</label>
                  <textarea
                    id="address"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    rows="3"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="area">Area</label>
                  <input
                    type="text"
                    id="area"
                    name="area"
                    value={formData.area}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="zone">Zone</label>
                  <input
                    type="text"
                    id="zone"
                    name="zone"
                    value={formData.zone}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="district">District</label>
                  <input
                    type="text"
                    id="district"
                    name="district"
                    value={formData.district}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="city">City</label>
                  <input
                    type="text"
                    id="city"
                    name="city"
                    value={formData.city}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="pin">Pin</label>
                  <input
                    type="text"
                    id="pin"
                    name="pin"
                    value={formData.pin}
                    onChange={handleInputChange}
                    className={validationErrors.pin ? 'error' : ''}
                    placeholder="e.g., 110001"
                  />
                  {validationErrors.pin && (
                    <span className="error-message">{validationErrors.pin}</span>
                  )}
                </div>
                <div className="form-group">
                  <label htmlFor="phone">Phone</label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={validationErrors.phone ? 'error' : ''}
                    placeholder="e.g., 9876543210"
                  />
                  {validationErrors.phone && (
                    <span className="error-message">{validationErrors.phone}</span>
                  )}
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={validationErrors.email ? 'error' : ''}
                    placeholder="e.g., store@example.com"
                  />
                  {validationErrors.email && (
                    <span className="error-message">{validationErrors.email}</span>
                  )}
                </div>
                <div className="form-group">
                  <label htmlFor="gstNumber">GST Number</label>
                  <input
                    type="text"
                    id="gstNumber"
                    name="gstNumber"
                    value={formData.gstNumber}
                    onChange={handleInputChange}
                  />
                </div>
              </div>
              <div className="form-actions">
                <button type="button" className="cancel-btn" onClick={handleCloseModal}>
                  Cancel
                </button>
                <button type="submit" className="save-btn">
                  {editingStore ? 'Update Store' : 'Add Store'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default StoreList;
