import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ScanBarcode, Trash2, Save, X, Store, Calendar, FileText, User, ArrowLeft } from 'lucide-react';

const SalesEntry = () => {
    const navigate = useNavigate();

    // --- State ---
    // Header
    const [parties, setParties] = useState([]);
    const [selectedParty, setSelectedParty] = useState('');
    const [invoiceDate, setInvoiceDate] = useState(new Date().toISOString().split('T')[0]);
    const [invoiceNo, setInvoiceNo] = useState('New');
    const [storeInfo, setStoreInfo] = useState(null);

    // Grid State
    const [activeSizes, setActiveSizes] = useState([]);
    const [gridRows, setGridRows] = useState([]);

    // Scan Line State
    const [scanItemCode, setScanItemCode] = useState('');
    const [scanItemName, setScanItemName] = useState('');
    const [scanQuantities, setScanQuantities] = useState({});
    const [scanMrps, setScanMrps] = useState({});
    const scanInputRef = useRef(null);

    // Footer
    const [tenderType, setTenderType] = useState('Cash');
    const [totalAmount, setTotalAmount] = useState(0);

    // --- Helpers ---
    const calculateGrandTotal = useCallback(() => {
        const total = gridRows.reduce((sum, row) => sum + (row.rowTotal || 0), 0);
        setTotalAmount(total);
    }, [gridRows]);

    // --- Effects ---
    useEffect(() => {
        fetchParties();
        fetchNextInvoiceNo();
        fetchStoreInfo();
        fetchActiveSizes();
    }, []);

    useEffect(() => {
        calculateGrandTotal();
    }, [calculateGrandTotal]);

    // --- API Calls ---
    const fetchActiveSizes = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('/api/sizes/active', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setActiveSizes(response.data || []);
        } catch (error) {
            console.error("Error fetching sizes", error);
        }
    };

    const fetchAllItems = async () => {
        // Not used in Grid mode effectively, but keeping if needed for lookup
    };

    const fetchParties = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/parties');
            setParties(response.data);
        } catch (error) {
            console.error("Error fetching parties", error);
        }
    };

    const fetchNextInvoiceNo = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/generate-invoice-no');
            setInvoiceNo(response.data);
        } catch (error) {
            console.error("Error fetching invoice no", error);
        }
    };

    const fetchStoreInfo = async () => {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (user.userName) {
            try {
                const response = await axios.get(`http://localhost:8080/api/stores/by-user/${user.userName}`);
                if (response.data.success && response.data.stores && response.data.stores.length > 0) {
                    setStoreInfo(response.data.stores[0]);
                }
            } catch (error) {
                console.error("Error fetching store info", error);
            }
        }
    };

    const fetchItemPrices = async (itemCode, rowIndex) => {
        if (!itemCode) return;

        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`/api/prices/item/${itemCode}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (response.data.success && response.data.prices) {
                const pricesMap = {};
                let itemName = '';

                response.data.prices.forEach(p => {
                    pricesMap[p.sizeCode] = p.mrp;
                    if (p.itemName) itemName = p.itemName;
                });

                setGridRows(prev => {
                    const newRows = [...prev];
                    const currentRow = newRows[rowIndex];

                    // Recalculate total with new prices
                    let newRowTotal = 0;
                    Object.keys(currentRow.quantities).forEach(sizeCode => {
                        const qty = currentRow.quantities[sizeCode] || 0;
                        const mrp = pricesMap[sizeCode] || 0;
                        newRowTotal += qty * mrp;
                    });

                    newRows[rowIndex] = {
                        ...currentRow,
                        itemName: itemName || currentRow.itemName,
                        mrps: pricesMap,
                        rowTotal: newRowTotal
                    };
                    return newRows;
                });
            }
        } catch (error) {
            console.error("Error fetching item prices", error);
        }
    };

    // --- Handlers ---
    const fetchScanItemDetails = async (code) => {
        if (!code) return;
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`/api/prices/item/${code}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (response.data.success && response.data.prices) {
                const pricesMap = {};
                let itemName = '';

                response.data.prices.forEach(p => {
                    pricesMap[p.sizeCode] = p.mrp;
                    if (p.itemName) itemName = p.itemName;
                });

                setScanItemName(itemName);
                setScanMrps(pricesMap);
            }
        } catch (error) {
            console.error("Error fetching item prices", error);
        }
    };

    const handleScanItemCodeBlur = () => {
        if (scanItemCode) {
            fetchScanItemDetails(scanItemCode);
        }
    };

    const handleScanItemKeyDown = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault();
            handleScanItemCodeBlur();
            // Optional: Focus first quantity input
        }
    };

    const handleScanQuantityChange = (sizeCode, value) => {
        const qty = parseInt(value) || 0;
        setScanQuantities(prev => ({ ...prev, [sizeCode]: qty }));
    };

    const handleAddItem = () => {
        if (!scanItemCode) {
            alert("Please enter an Item Code");
            if (scanInputRef.current) scanInputRef.current.focus();
            return;
        }

        // Calculate row total
        let rowTotal = 0;
        let hasQty = false;

        Object.keys(scanQuantities).forEach(sizeCode => {
            const qty = scanQuantities[sizeCode] || 0;
            if (qty > 0) hasQty = true;
            rowTotal += qty * (scanMrps[sizeCode] || 0);
        });

        if (!hasQty) {
            alert("Please enter at least one quantity");
            return;
        }

        const newRow = {
            id: Date.now(),
            itemCode: scanItemCode,
            itemName: scanItemName,
            quantities: { ...scanQuantities },
            mrps: { ...scanMrps },
            rowTotal: rowTotal
        };

        setGridRows(prev => [...prev, newRow]);

        // Reset scan line
        setScanItemCode('');
        setScanItemName('');
        setScanQuantities({});
        setScanMrps({});

        if (scanInputRef.current) scanInputRef.current.focus();
    };

    const handleItemCodeChange = (index, value) => {
        setGridRows(prev => {
            const newRows = [...prev];
            newRows[index] = { ...newRows[index], itemCode: value };
            return newRows;
        });
    };

    const handleItemCodeBlur = (index, value) => {
        if (value) {
            fetchItemPrices(value, index);
        }
    };

    const handleQuantityChange = (rowIndex, sizeCode, value) => {
        const qty = parseInt(value) || 0;

        setGridRows(prev => {
            const newRows = [...prev];
            const currentRow = newRows[rowIndex];

            const newQuantities = { ...currentRow.quantities, [sizeCode]: qty };

            // Recalculate row total
            let newRowTotal = 0;
            activeSizes.forEach(size => {
                const sCode = size.code;
                const q = sCode === sizeCode ? qty : (newQuantities[sCode] || 0);
                const mrp = currentRow.mrps[sCode] || 0;
                newRowTotal += q * mrp;
            });

            newRows[rowIndex] = {
                ...currentRow,
                quantities: newQuantities,
                rowTotal: newRowTotal
            };
            return newRows;
        });
    };

    const handleAddRow = () => {
        setGridRows(prev => [
            ...prev,
            { id: Date.now(), itemCode: '', itemName: '', quantities: {}, mrps: {}, rowTotal: 0 }
        ]);
    };

    const handleRemoveRow = (index) => {
        if (gridRows.length > 1) {
            setGridRows(prev => prev.filter((_, i) => i !== index));
        }
    };

    const handleSave = async () => {
        if (!selectedParty) {
            alert('Please select a Party Name');
            return;
        }

        const itemsPayload = [];
        gridRows.forEach(row => {
            if (!row.itemCode) return;

            Object.keys(row.quantities).forEach(sizeCode => {
                const qty = row.quantities[sizeCode];
                if (qty > 0) {
                    itemsPayload.push({
                        itemCode: row.itemCode,
                        sizeCode: sizeCode,
                        mrp: row.mrps[sizeCode] || 0,
                        quantity: qty,
                        amount: qty * (row.mrps[sizeCode] || 0)
                    });
                }
            });
        });

        if (itemsPayload.length === 0) {
            alert('Please add at least one item');
            return;
        }

        if (!tenderType) {
            alert('Please select a Tender Type');
            return;
        }

        if (!storeInfo || !storeInfo.storeCode) {
            alert('Store information missing. Cannot save.');
            return;
        }

        const payload = {
            invoiceNo,
            invoiceDate,
            partyCode: selectedParty,
            totalAmount,
            tenderType,
            storeCode: storeInfo.storeCode,
            items: itemsPayload
        };

        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:8080/api/sales/save', payload, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            alert('Invoice saved successfully');
            resetForm();
        } catch (error) {
            console.error("Error saving invoice", error);
            alert('Failed to save invoice');
        }
    };

    const resetForm = () => {
        setGridRows([{ id: Date.now(), itemCode: '', itemName: '', quantities: {}, mrps: {}, rowTotal: 0 }]);
        setSelectedParty('');
        setTenderType('Cash');
        setInvoiceDate(new Date().toISOString().split('T')[0]);
        fetchNextInvoiceNo();
    };

    const handleCancel = () => {
        if (window.confirm('Do you want to cancel this invoice?')) {
            resetForm();
        }
    };

    return (
        <div className="min-h-screen bg-slate-50 p-2 flex flex-col items-center justify-center font-sans">
            <div className="w-full max-w-[95%] bg-white rounded-xl shadow-sm overflow-hidden flex flex-col h-[95vh]">

                {/* Top Bar */}
                <div className="bg-white px-4 py-2 border-b border-slate-100 flex items-center justify-between relative shrink-0">
                    <div className="flex items-center gap-3">
                        <button 
                            onClick={() => navigate('/store-dashboard')}
                            className="p-1.5 hover:bg-slate-100 rounded-full text-slate-500 transition-colors"
                            title="Back to Dashboard"
                        >
                            <ArrowLeft className="w-5 h-5" />
                        </button>
                        <h2 className="text-lg font-bold text-slate-800 flex items-center gap-2">
                            Sales Voucher
                        </h2>
                    </div>

                    {storeInfo && (
                        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 flex items-center gap-2 text-xs font-medium text-slate-500 bg-slate-50 px-3 py-1 rounded-full border border-slate-100">
                            <Store className="w-3 h-3" />
                            <span>{storeInfo.storeCode} - {storeInfo.storeName}</span>
                        </div>
                    )}
                </div>

                {/* Header Fields */}
                <div className="p-3 border-b border-slate-100 bg-white grid grid-cols-1 md:grid-cols-4 gap-3 shrink-0">
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <User className="w-3 h-3" /> Party Name
                        </label>
                        <select 
                            className="w-full bg-slate-100 border-transparent focus:bg-white focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1.5 text-sm text-slate-700 transition-all outline-none h-9"
                            value={selectedParty}
                            onChange={(e) => setSelectedParty(e.target.value)}
                        >
                            <option value="">Select Party</option>
                            {parties.map(party => (
                                <option key={party.id} value={party.partyCode}>
                                    {party.partyName}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <Calendar className="w-3 h-3" /> Invoice Date
                        </label>
                        <input 
                            type="date" 
                            className="w-full bg-slate-100 border-transparent focus:bg-white focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1.5 text-sm text-slate-700 transition-all outline-none h-9"
                            value={invoiceDate}
                            onChange={(e) => setInvoiceDate(e.target.value)}
                        />
                    </div>

                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            <FileText className="w-3 h-3" /> Invoice Number
                        </label>
                        <input 
                            type="text" 
                            className="w-full bg-slate-100 border-transparent rounded px-2 py-1.5 text-sm text-slate-500 font-medium cursor-not-allowed outline-none h-9"
                            value={invoiceNo}
                            readOnly
                        />
                    </div>

                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-slate-500 uppercase tracking-wider flex items-center gap-1">
                            Tender Type
                        </label>
                        <select 
                            className="w-full bg-slate-100 border-transparent focus:bg-white focus:ring-2 focus:ring-indigo-500 rounded px-2 py-1.5 text-sm text-slate-700 transition-all outline-none h-9"
                            value={tenderType}
                            onChange={(e) => setTenderType(e.target.value)}
                        >
                            <option value="Cash">Cash</option>
                            <option value="Card">Card</option>
                            <option value="UPI">UPI</option>
                        </select>
                    </div>
                </div>

                {/* Grid Body */}
                <div className="flex-1 overflow-auto bg-white min-h-0 relative">
                    <table className="w-full text-left border-collapse relative">
                        <thead className="bg-white sticky top-0 z-20 shadow-sm">
                            <tr>
                                <th className="px-2 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 w-10 text-center">#</th>
                                <th className="px-2 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 min-w-[150px]">Item Code</th>
                                {activeSizes.map(size => (
                                    <th key={size.code} className="px-2 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 text-center min-w-[80px]">
                                        {size.name}
                                        <div className="text-[10px] text-slate-400 font-normal">Qty / MRP</div>
                                    </th>
                                ))}
                                <th className="px-2 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 w-24 text-right">Total</th>
                                <th className="px-2 py-2 text-xs font-semibold text-slate-500 uppercase tracking-wider border-b border-slate-100 w-10"></th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-slate-50">
                            {gridRows.map((row, index) => (
                                <tr key={row.id} className="hover:bg-slate-50/50 transition-colors group">
                                    <td className="px-2 py-1 text-xs text-slate-400 text-center">{index + 1}</td>
                                    <td className="px-2 py-1">
                                        <div className="space-y-1">
                                            <div className="relative">
                                                <ScanBarcode className="absolute left-2 top-1/2 -translate-y-1/2 w-3.5 h-3.5 text-slate-400" />
                                                <input 
                                                    type="text" 
                                                    className="w-full pl-7 pr-2 py-1 bg-white border border-slate-200 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 rounded text-sm transition-all outline-none h-8"
                                                    value={row.itemCode}
                                                    onChange={(e) => handleItemCodeChange(index, e.target.value)}
                                                    onBlur={(e) => handleItemCodeBlur(index, e.target.value)}
                                                    placeholder="Scan Item"
                                                />
                                            </div>
                                            {row.itemName && (
                                                <div className="text-xs text-slate-500 truncate max-w-[150px] pl-1" title={row.itemName}>
                                                    {row.itemName}
                                                </div>
                                            )}
                                        </div>
                                    </td>
                                    {activeSizes.map(size => (
                                        <td key={size.code} className="px-2 py-1">
                                            <div className="flex flex-col gap-1">
                                                <input 
                                                    type="number"
                                                    className="w-full px-2 py-1 text-center bg-white border border-slate-200 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 rounded text-sm transition-all outline-none h-8"
                                                    value={row.quantities[size.code] || ''}
                                                    onChange={(e) => handleQuantityChange(index, size.code, e.target.value)}
                                                    placeholder="-"
                                                    min="0"
                                                />
                                                <div className="text-[10px] text-center text-slate-400 h-3">
                                                    {row.mrps[size.code] ? `â‚¹${row.mrps[size.code]}` : '-'}
                                                </div>
                                            </div>
                                        </td>
                                    ))}
                                    <td className="px-2 py-1 text-right font-medium text-slate-700">
                                        â‚¹{row.rowTotal.toFixed(2)}
                                    </td>
                                    <td className="px-2 py-1 text-right">
                                        <button 
                                            onClick={() => handleRemoveRow(index)}
                                            className="text-slate-300 hover:text-red-500 transition-colors p-1 opacity-0 group-hover:opacity-100"
                                            title="Remove Row"
                                        >
                                            <X className="w-4 h-4" />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <button 
                        onClick={handleAddRow}
                        className="m-4 px-4 py-2 bg-slate-100 hover:bg-slate-200 text-slate-600 rounded-md text-sm font-medium transition-colors flex items-center gap-2"
                    >
                        + Add Row
                    </button>
                </div>

                {/* Footer Totals */}
                <div className="bg-slate-50 p-4 border-t border-slate-200 shrink-0">
                    <div className="flex items-center justify-between max-w-xs ml-auto">
                        <span className="text-slate-500 font-medium uppercase text-xs tracking-wider">Grand Total</span>
                        <span className="text-2xl font-bold text-slate-800">â‚¹ {totalAmount.toFixed(2)}</span>
                    </div>
                    <div className="mt-4 flex justify-end gap-3">
                        <button 
                            onClick={handleCancel}
