import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ScanBarcode, Trash2, Save, X, Store, Calendar, FileText, User, ArrowLeft, Plus } from 'lucide-react';

const SalesEntry = () => {
    const navigate = useNavigate();

    // --- State ---
    // Header
    const [parties, setParties] = useState([]);
    const [selectedParty, setSelectedParty] = useState('');
    const [invoiceDate, setInvoiceDate] = useState(new Date().toISOString().split('T')[0]);
    const [invoiceNo, setInvoiceNo] = useState('New');
    const [storeInfo, setStoreInfo] = useState(null);

    // Grid State
    const [activeSizes, setActiveSizes] = useState([]);
    const [gridRows, setGridRows] = useState([]);

    // Scan Line State
    const [scanItemCode, setScanItemCode] = useState('');
    const [scanItemName, setScanItemName] = useState('');
    const [scanQuantities, setScanQuantities] = useState({});
    const [scanMrps, setScanMrps] = useState({});
    const scanInputRef = useRef(null);
    const [searchResults, setSearchResults] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);

    // Footer
    const [cashAmount, setCashAmount] = useState('');
    const [cardAmount, setCardAmount] = useState('');
    const [upiAmount, setUpiAmount] = useState('');
    const [totalAmount, setTotalAmount] = useState(0); // Grand Total (from items)

    // Footer Extended State
    const [addCanteen, setAddCanteen] = useState(0);
    const [conveyance, setConveyance] = useState(0);
    const [foodSalesman, setFoodSalesman] = useState(0);

    // Derived Footer Values
    const totalSale = totalAmount + (parseFloat(addCanteen) || 0);
    const totalExp = (parseFloat(conveyance) || 0) + (parseFloat(foodSalesman) || 0);
    const totalCollection = totalSale - totalExp;

    // --- Helpers ---
    const calculateGrandTotal = useCallback(() => {
        const total = gridRows.reduce((sum, row) => sum + (row.rowTotal || 0), 0);
        setTotalAmount(total);
    }, [gridRows]);

    // --- Effects ---
    useEffect(() => {
        fetchParties();
        fetchNextInvoiceNo();
        fetchStoreInfo();
        fetchActiveSizes();
    }, []);

    useEffect(() => {
        calculateGrandTotal();
    }, [calculateGrandTotal]);

    // --- API Calls ---
    const fetchActiveSizes = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('/api/sizes/active', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setActiveSizes(response.data || []);
        } catch (error) {
            console.error("Error fetching sizes", error);
        }
    };

    const fetchAllItems = async () => {
        // Not used in Grid mode effectively, but keeping if needed for lookup
    };

    const fetchParties = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/parties');
            setParties(response.data);
        } catch (error) {
            console.error("Error fetching parties", error);
        }
    };

    const fetchNextInvoiceNo = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/generate-invoice-no');
            setInvoiceNo(response.data);
        } catch (error) {
            console.error("Error fetching invoice no", error);
        }
    };

    const fetchStoreInfo = async () => {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (user.userName) {
            try {
                const response = await axios.get(`http://localhost:8080/api/stores/by-user/${user.userName}`);
                if (response.data.success && response.data.stores && response.data.stores.length > 0) {
                    setStoreInfo(response.data.stores[0]);
                }
            } catch (error) {
                console.error("Error fetching store info", error);
            }
        }
    };

    const fetchItemPrices = async (itemCode, rowIndex) => {
        if (!itemCode) return;

        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`/api/prices/item/${itemCode}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (response.data.success && response.data.prices) {
                const pricesMap = {};
                let itemName = '';

                response.data.prices.forEach(p => {
                    pricesMap[p.sizeCode] = p.mrp;
                    if (p.itemName) itemName = p.itemName;
                });

                setGridRows(prev => {
                    const newRows = [...prev];
                    const currentRow = newRows[rowIndex];

                    // Recalculate total with new prices
                    let newRowTotal = 0;
                    Object.keys(currentRow.quantities).forEach(sizeCode => {
                        const qty = currentRow.quantities[sizeCode] || 0;
                        const mrp = pricesMap[sizeCode] || 0;
                        newRowTotal += qty * mrp;
                    });

                    newRows[rowIndex] = {
                        ...currentRow,
                        itemName: itemName || currentRow.itemName,
                        mrps: pricesMap,
                        rowTotal: newRowTotal
                    };
                    return newRows;
                });
            }
        } catch (error) {
            console.error("Error fetching item prices", error);
        }
    };

    // --- Handlers ---
    const fetchScanItemDetails = async (code) => {
        if (!code) return;
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`/api/prices/item/${code}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (response.data.success && response.data.prices) {
                const pricesMap = {};
                let itemName = '';

                response.data.prices.forEach(p => {
                    pricesMap[p.sizeCode] = p.mrp;
                    if (p.itemName) itemName = p.itemName;
                });

                setScanItemName(itemName);
                setScanMrps(pricesMap);
            }
        } catch (error) {
            console.error("Error fetching item prices", error);
        }
    };

    const handleScanItemCodeBlur = () => {
        // Delay hiding suggestions to allow click to register
        setTimeout(() => {
            setShowSuggestions(false);
            if (scanItemCode && !scanItemName) {
                // If code entered but no name (no selection), try to fetch by code
                fetchScanItemDetails(scanItemCode);
            }
        }, 200);
    };

    const handleScanInputChange = async (e) => {
        const value = e.target.value;
        setScanItemCode(value);

        if (value.length > 1) {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:8080/api/items/search?query=${value}`, {
                     headers: { 'Authorization': `Bearer ${token}` }
                });
                if (response.data.success) {
                    setSearchResults(response.data.items || []);
                    setShowSuggestions(true);
                }
            } catch (error) {
                console.error("Search error", error);
            }
        } else {
            setSearchResults([]);
            setShowSuggestions(false);
        }
    };

    const handleSelectSuggestion = (item) => {
        setScanItemCode(item.itemCode);
        setScanItemName(item.itemName);
        setShowSuggestions(false);
        fetchScanItemDetails(item.itemCode);
    };

    const handleScanItemKeyDown = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault();
            handleScanItemCodeBlur();
            // Optional: Focus first quantity input
        }
    };

    const handleScanQuantityChange = (sizeCode, value) => {
        const qty = parseInt(value) || 0;
        setScanQuantities(prev => ({ ...prev, [sizeCode]: qty }));
    };

    const handleAddItem = () => {
        if (!scanItemCode) {
            alert("Please enter an Item Code");
            if (scanInputRef.current) scanInputRef.current.focus();
            return;
        }

        // Check for quantities
        let hasQty = false;
        Object.keys(scanQuantities).forEach(sizeCode => {
            if ((scanQuantities[sizeCode] || 0) > 0) hasQty = true;
        });

        if (!hasQty) {
            alert("Please enter at least one quantity");
            return;
        }

        setGridRows(prev => {
            const existingIndex = prev.findIndex(row => row.itemCode === scanItemCode);

            if (existingIndex !== -1) {
                // Update existing row
                const newRows = [...prev];
                const existingRow = newRows[existingIndex];

                // Merge quantities and MRPs
                const newQuantities = { ...existingRow.quantities };
                const newMrps = { ...existingRow.mrps, ...scanMrps }; // Update MRPs just in case

                Object.keys(scanQuantities).forEach(sizeCode => {
                    const scanQty = scanQuantities[sizeCode] || 0;
                    if (scanQty > 0) {
                        newQuantities[sizeCode] = (newQuantities[sizeCode] || 0) + scanQty;
                    }
                });

                // Recalculate Total
                let newRowTotal = 0;
                Object.keys(newQuantities).forEach(sizeCode => {
                    const qty = newQuantities[sizeCode] || 0;
                    const mrp = newMrps[sizeCode] || 0;
                    newRowTotal += qty * mrp;
                });

                newRows[existingIndex] = {
                    ...existingRow,
                    itemName: scanItemName || existingRow.itemName,
                    quantities: newQuantities,
                    mrps: newMrps,
                    rowTotal: newRowTotal
                };
                return newRows;
            } else {
                // Add new row
                let rowTotal = 0;
                Object.keys(scanQuantities).forEach(sizeCode => {
                    const qty = scanQuantities[sizeCode] || 0;
                    rowTotal += qty * (scanMrps[sizeCode] || 0);
                });

                const newRow = {
                    id: Date.now(),
                    itemCode: scanItemCode,
                    itemName: scanItemName,
                    quantities: { ...scanQuantities },
                    mrps: { ...scanMrps },
                    rowTotal: rowTotal
                };
                return [...prev, newRow];
            }
        });

        // Reset scan line
        setScanItemCode('');
        setScanItemName('');
        setScanQuantities({});
        setScanMrps({});

        if (scanInputRef.current) scanInputRef.current.focus();
    };

    const handleItemCodeChange = (index, value) => {
        setGridRows(prev => {
            const newRows = [...prev];
            newRows[index] = { ...newRows[index], itemCode: value };
            return newRows;
        });
    };

    const handleItemCodeBlur = (index, value) => {
        if (value) {
            fetchItemPrices(value, index);
        }
    };

    const handleQuantityChange = (rowIndex, sizeCode, value) => {
        const qty = parseInt(value) || 0;

        setGridRows(prev => {
            const newRows = [...prev];
            const currentRow = newRows[rowIndex];

            const newQuantities = { ...currentRow.quantities, [sizeCode]: qty };

            // Recalculate row total
            let newRowTotal = 0;
            activeSizes.forEach(size => {
                const sCode = size.code;
                const q = sCode === sizeCode ? qty : (newQuantities[sCode] || 0);
                const mrp = currentRow.mrps[sCode] || 0;
                newRowTotal += q * mrp;
            });

            newRows[rowIndex] = {
                ...currentRow,
                quantities: newQuantities,
                rowTotal: newRowTotal
            };
            return newRows;
        });
    };

    const handleRemoveRow = (index) => {
        setGridRows(prev => prev.filter((_, i) => i !== index));
    };

    const handleSave = async () => {
        if (!selectedParty) {
            alert('Please select a Party Name');
            return;
        }

        const itemsPayload = [];
        gridRows.forEach(row => {
            if (!row.itemCode) return;

            Object.keys(row.quantities).forEach(sizeCode => {
                const qty = row.quantities[sizeCode];
                if (qty > 0) {
                    itemsPayload.push({
                        itemCode: row.itemCode,
                        sizeCode: sizeCode,
                        mrp: row.mrps[sizeCode] || 0,
                        quantity: qty,
                        amount: qty * (row.mrps[sizeCode] || 0)
                    });
                }
            });
        });

        if (itemsPayload.length === 0) {
            alert('Please add at least one item');
            return;
        }

        if (!storeInfo || !storeInfo.storeCode) {
            alert('Store information missing. Cannot save.');
            return;
        }

        // Validate Total Payment vs Collection
        let totalPayment = 0;
        Object.values(tenderAmounts).forEach(val => totalPayment += (parseFloat(val) || 0));

        if (totalPayment > totalCollection + 0.01) { // Adding small epsilon for float precision
            alert(`Total payment breakdown (₹${totalPayment.toFixed(2)}) cannot exceed Total Collection (₹${totalCollection.toFixed(2)})`);
            return;
        }

        // Map dynamic tender amounts to DTO fields (temporary backward compatibility if needed)
        // Or simply send them as map if backend supports it. 
        // Based on user request "Add tender type at Sales Voucher in place of given screenshot",
        // we assume backend might need updates to support generic tender types or we map specific ones.
        // The original code had cashAmount, cardAmount, upiAmount.
        // I will map them for now to ensure compatibility with existing backend Controller DTO.

        const cashAmount = parseFloat(tenderAmounts['CASH']) || 0;
        const cardAmount = parseFloat(tenderAmounts['CARD']) || 0;
        const upiAmount = parseFloat(tenderAmounts['UPI']) || 0;

        const payload = {
            invoiceNo,
            invoiceDate,
            partyCode: selectedParty,
            totalAmount,
            tenderType: 'Split', // Default or derived
            storeCode: storeInfo.storeCode,
            addCanteen: parseFloat(addCanteen) || 0,
            conveyance: parseFloat(conveyance) || 0,
            foodSalesman: parseFloat(foodSalesman) || 0,
            cashAmount,
            cardAmount,
            upiAmount,
            items: itemsPayload
        };

        try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:8080/api/sales/save', payload, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            alert('Invoice saved successfully');
            resetForm();
        } catch (error) {
            console.error("Error saving invoice", error);
            alert('Failed to save invoice');
        }
    };

    const resetForm = () => {
        setGridRows([]);
        setScanItemCode('');
        setScanItemName('');
        setScanQuantities({});
        setScanMrps({});
        setAddCanteen(0);
        setConveyance(0);
        setFoodSalesman(0);

        // Reset tender amounts
        const resetAmounts = {};
        Object.keys(tenderAmounts).forEach(k => resetAmounts[k] = '');
        setTenderAmounts(resetAmounts);

        setSelectedParty('');
        setInvoiceDate(new Date().toISOString().split('T')[0]);
        fetchNextInvoiceNo();
        if (scanInputRef.current) scanInputRef.current.focus();
    };

    const handleCancel = () => {
        if (window.confirm('Do you want to cancel this invoice?')) {
            resetForm();
        }
    };

    return (
        <div className="min-h-screen bg-slate-50 p-2 flex flex-col items-center justify-center font-sans">
            <div className="w-full max-w-[95%] bg-white rounded-xl shadow-sm overflow-hidden flex flex-col h-[95vh]">

                {/* Top Bar */}
                <div className="bg-white px-3 py-1 border-b border-slate-100 flex items-center justify-between relative shrink-0 h-10">
                    <div className="flex items-center gap-2">
                        <button 
                            onClick={() => navigate('/store-dashboard')}
                            className="p-1 hover:bg-slate-100 rounded-full text-slate-500 transition-colors"
                            title="Back to Dashboard"
                        >
                            <ArrowLeft className="w-4 h-4" />
                        </button>
                        <h2 className="text-sm font-bold text-slate-800 flex items-center gap-2">
                            Sales Voucher
                        </h2>
                    </div>

                    <div className="absolute left-1/2 transform -translate-x-1/2 flex items-center gap-6">
                        <div className="flex items-center gap-2 bg-slate-50 px-3 py-0.5 rounded-lg border border-slate-100">
                            <User className="w-3.5 h-3.5 text-indigo-500" />
                            <select 
                                className="bg-transparent text-xs font-semibold text-slate-700 outline-none min-w-[150px]"
                                value={selectedParty}
                                onChange={(e) => setSelectedParty(e.target.value)}
                            >
                                <option value="">Select Party</option>
                                {parties.map(party => (
                                    <option key={party.id} value={party.partyCode}>
                                        {party.partyName}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="flex items-center gap-2 bg-slate-50 px-3 py-0.5 rounded-lg border border-slate-100">
                            <Calendar className="w-3.5 h-3.5 text-indigo-500" />
                            <input 
                                type="date" 
                                className="bg-transparent text-xs font-semibold text-slate-700 outline-none"
                                value={invoiceDate}
                                onChange={(e) => setInvoiceDate(e.target.value)}
                            />
                        </div>
                    </div>

                    <div className="flex items-center gap-3">
                        <div className="flex flex-col items-end mr-2">
                            <span className="text-[10px] text-slate-500 uppercase tracking-wider font-medium">Invoice No</span>
                            <span className="text-xs font-bold text-indigo-600 font-mono bg-indigo-50 px-2 py-0.5 rounded">{invoiceNo}</span>
                        </div>
                        {storeInfo && (
                            <div className="flex items-center gap-1.5 bg-slate-100 px-2 py-1 rounded text-slate-600">
                                <Store className="w-3.5 h-3.5" />
                                <span className="text-xs font-medium">{storeInfo.storeName}</span>
                            </div>
                        )}
                    </div>
                </div>

                {/* Scan Line */}
                <div className="bg-slate-50 px-3 py-2 border-b border-slate-200 shrink-0">
                    <div className="flex items-end gap-3 max-w-6xl mx-auto w-full">
                        <div className="relative w-32">
                            <label className="text-[10px] font-bold text-slate-500 mb-0.5 block uppercase tracking-wider">Item Code</label>
                            <div className="relative">
                                <ScanBarcode className="absolute left-2 top-1.5 w-3.5 h-3.5 text-slate-400" />
                                <input 
                                    ref={scanInputRef}
                                    type="text" 
                                    className="w-full pl-7 pr-2 py-1 text-xs border border-slate-300 rounded focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-all font-mono uppercase bg-white h-7"
                                    placeholder="Scan/Type"
                                    value={scanItemCode}
                                    onChange={handleScanInputChange}
                                    onKeyDown={handleScanItemKeyDown}
                                    onBlur={handleScanItemCodeBlur}
                                />
                                {showSuggestions && searchResults.length > 0 && (
                                    <div className="absolute z-50 w-[300px] bg-white border border-slate-200 shadow-lg rounded-md mt-1 max-h-60 overflow-y-auto">
                                        {searchResults.map(item => (
                                            <div 
                                                key={item.itemCode}
                                                className="px-3 py-2 hover:bg-slate-50 cursor-pointer border-b border-slate-50 last:border-0"
                                                onClick={() => handleSelectSuggestion(item)}
                                            >
                                                <div className="font-medium text-xs text-slate-700">{item.itemName}</div>
                                                <div className="text-[10px] text-slate-500 flex justify-between mt-0.5">
                                                    <span>Code: {item.itemCode}</span>
                                                    <span>Price: ₹{item.salePrice}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>

                        <div className="flex-1 min-w-[200px]">
                            <label className="text-[10px] font-bold text-slate-500 mb-0.5 block uppercase tracking-wider">Item Name</label>
                            <div className="w-full px-3 py-1.5 bg-slate-200 rounded border border-slate-200 text-xs text-slate-600 font-medium h-7 truncate">
                                {scanItemName || '-'}
                            </div>
                        </div>

                        {/* Dynamic Size Inputs */}
                        <div className="flex gap-2">
                            {activeSizes.map(size => (
                                <div key={size.id} className="w-16">
                                    <label className="text-[10px] font-bold text-slate-500 mb-0.5 block text-center uppercase tracking-wider">{size.label}</label>
                                    <div className="relative">
                                        <input 
                                            type="number" 
                                            className="w-full px-1 py-1 text-xs text-center border border-slate-300 rounded focus:ring-2 focus:ring-indigo-500 outline-none h-7"
                                            placeholder="Qty"
                                            value={scanQuantities[size.code] || ''}
                                            onChange={(e) => handleScanQuantityChange(size.code, e.target.value)}
                                            onKeyDown={(e) => e.key === 'Enter' && handleAddItem()}
                                        />
                                        {scanMrps[size.code] && (
                                            <div className="absolute -bottom-3 left-0 right-0 text-[9px] text-center text-slate-400">
                                                ₹{scanMrps[size.code]}
                                            </div>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>

                        <button 
                            onClick={handleAddItem}
                            className="bg-indigo-600 hover:bg-indigo-700 text-white p-1.5 rounded shadow-sm transition-colors mb-0.5 h-7 w-7 flex items-center justify-center"
                            title="Add Item"
                        >
                            <Plus className="w-4 h-4" />
                        </button>
                    </div>
                </div>

                {/* Grid */}
                <div className="flex-1 overflow-auto bg-white relative">
                    <table className="w-full text-left border-collapse">
                        <thead className="bg-slate-50 sticky top-0 z-10 shadow-sm">
                            <tr>
                                <th className="py-2 px-3 text-[10px] font-bold text-slate-500 uppercase tracking-wider w-10 text-center border-b border-slate-200">#</th>
                                <th className="py-2 px-3 text-[10px] font-bold text-slate-500 uppercase tracking-wider w-28 border-b border-slate-200">Item Code</th>
                                <th className="py-2 px-3 text-[10px] font-bold text-slate-500 uppercase tracking-wider border-b border-slate-200">Item Name</th>
                                {activeSizes.map(size => (
                                    <th key={size.id} className="py-2 px-2 text-[10px] font-bold text-slate-500 uppercase tracking-wider text-center w-20 border-b border-slate-200">
                                        {size.label}
                                        <div className="text-[8px] font-normal text-slate-400 lowercase">qty / mrp</div>
                                    </th>
                                ))}
                                <th className="py-2 px-3 text-[10px] font-bold text-slate-500 uppercase tracking-wider text-right w-24 border-b border-slate-200">Total</th>
                                <th className="py-2 px-2 text-[10px] font-bold text-slate-500 uppercase tracking-wider w-10 border-b border-slate-200"></th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-slate-100">
                            {gridRows.length === 0 ? (
                                <tr>
                                    <td colSpan={5 + activeSizes.length} className="py-10 text-center text-slate-400 text-sm">
                                        No items added yet. Scan or type an item code to begin.
                                    </td>
                                </tr>
                            ) : gridRows.map((row, index) => (
                                <tr key={row.id} className="hover:bg-slate-50 transition-colors group">
                                    <td className="py-1.5 px-3 text-xs text-slate-400 text-center">{index + 1}</td>
                                    <td className="py-1.5 px-3">
                                        <input 
                                            type="text" 
                                            className="w-full bg-transparent text-xs font-mono text-slate-700 outline-none border-b border-transparent focus:border-indigo-300 transition-colors"
                                            value={row.itemCode}
                                            onChange={(e) => handleItemCodeChange(index, e.target.value)}
                                            onBlur={(e) => handleItemCodeBlur(index, e.target.value)}
                                        />
                                    </td>
                                    <td className="py-1.5 px-3 text-xs font-medium text-slate-700">{row.itemName}</td>
                                    {activeSizes.map(size => (
                                        <td key={size.id} className="py-1.5 px-2 text-center">
                                            <div className="flex flex-col items-center">
                                                <input 
                                                    type="number" 
                                                    className="w-12 text-center text-xs bg-slate-50 border border-slate-200 rounded px-1 py-0.5 focus:ring-1 focus:ring-indigo-500 outline-none"
                                                    value={row.quantities[size.code] || ''}
                                                    onChange={(e) => handleQuantityChange(index, size.code, e.target.value)}
                                                />
                                                <span className="text-[9px] text-slate-400 mt-0.5">₹{row.mrps[size.code] || 0}</span>
                                            </div>
                                        </td>
                                    ))}
                                    <td className="py-1.5 px-3 text-right font-bold text-indigo-600 text-xs">
                                        ₹{row.rowTotal.toFixed(2)}
                                    </td>
                                    <td className="py-1.5 px-2 text-center">
                                        <button 
                                            onClick={() => handleRemoveRow(index)}
                                            className="p-1 text-slate-300 hover:text-red-500 transition-colors opacity-0 group-hover:opacity-100"
                                        >
                                            <Trash2 className="w-3.5 h-3.5" />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Footer Totals */}
                <div className="bg-slate-50 px-4 py-2 border-t border-slate-200 shrink-0">
                    <div className="grid grid-cols-3 gap-x-8 gap-y-1 max-w-5xl mx-auto w-full text-sm">

                        {/* Column 1 */}
                        <div className="contents">
                            {/* Row 1 */}
                            <div className="flex items-center justify-between">
                                <span className="font-medium text-slate-600">Grand Total</span>
                                <span className="font-bold text-slate-800">₹ {totalAmount.toFixed(2)}</span>
                            </div>
                            <div className="flex items-center justify-between">
                                <span className="font-medium text-slate-600">Conveyance</span>
                                <input 
                                    type="number"
                                    className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                    value={conveyance}
                                    onChange={(e) => setConveyance(e.target.value)}
                                    placeholder="0"
                                />
                            </div>
                            <div className="flex items-center justify-between">
                                <span className="font-bold text-slate-700">Total Collection</span>
                                <span className="font-bold text-green-700">₹ {totalCollection.toFixed(2)}</span>
                            </div>

                            {/* Row 2 */}
                            <div className="flex items-center justify-between">
                                <span className="font-medium text-slate-600">Add Canteen</span>
                                <input 
                                    type="number"
                                    className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                    value={addCanteen}
                                    onChange={(e) => setAddCanteen(e.target.value)}
                                    placeholder="0"
                                />
                            </div>
                            <div className="flex items-center justify-between">
                                <span className="font-medium text-slate-600">Food Salesman</span>
                                <input 
                                    type="number"
                                    className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                    value={foodSalesman}
                                    onChange={(e) => setFoodSalesman(e.target.value)}
                                    placeholder="0"
                                />
                            </div>

                            {/* Dynamic Tender Inputs - Slot 1 (Cash if available) */}
                            {tenderTypes.length > 0 && (
                                <div className="flex items-center justify-between">
                                    <span className="font-medium text-slate-600">{tenderTypes[0].name}</span>
                                    <input 
                                        type="number"
                                        className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                        value={tenderAmounts[tenderTypes[0].tenderCode] || ''}
                                        onChange={(e) => handlePaymentChange(tenderTypes[0].tenderCode, e.target.value)}
                                        placeholder="0"
                                    />
                                </div>
                            )}

                            {/* Row 3 */}
                            <div className="flex items-center justify-between">
                                <span className="font-bold text-slate-700">Total Sale</span>
                                <span className="font-bold text-indigo-700">₹ {totalSale.toFixed(2)}</span>
                            </div>
                            <div className="flex items-center justify-between">
                                <span className="font-bold text-slate-700">Total Exp</span>
                                <span className="font-bold text-red-600">₹ {totalExp.toFixed(2)}</span>
                            </div>

                            {/* Dynamic Tender Inputs - Slot 2 (Card if available) */}
                            {tenderTypes.length > 1 && (
                                <div className="flex items-center justify-between">
                                    <span className="font-medium text-slate-600">{tenderTypes[1].name}</span>
                                    <input 
                                        type="number"
                                        className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                        value={tenderAmounts[tenderTypes[1].tenderCode] || ''}
                                        onChange={(e) => handlePaymentChange(tenderTypes[1].tenderCode, e.target.value)}
                                        placeholder="0"
                                    />
                                </div>
                            )}

                            {/* Row 4 (Buttons) */}
                            <div></div>
                            <div className="flex items-center gap-2 w-full pt-1">
                                <button 
                                    onClick={handleCancel}
                                    className="flex-1 flex items-center justify-center px-3 py-1 bg-red-500 hover:bg-red-600 text-white font-medium rounded shadow-sm transition-colors text-xs h-7"
                                >
                                    Cancel
                                </button>
                                <button 
                                    onClick={handleSave}
                                    className="flex-1 flex items-center justify-center px-3 py-1 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded shadow-sm transition-colors gap-1 text-xs h-7"
                                >
                                    <Save className="w-3 h-3" /> Save Invoice
                                </button>
                            </div>

                            {/* Dynamic Tender Inputs - Slot 3 (UPI if available) */}
                            {tenderTypes.length > 2 && (
                                <div className="flex items-center justify-between">
                                    <span className="font-medium text-slate-600">{tenderTypes[2].name}</span>
                                    <input 
                                        type="number"
                                        className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                        value={tenderAmounts[tenderTypes[2].tenderCode] || ''}
                                        onChange={(e) => handlePaymentChange(tenderTypes[2].tenderCode, e.target.value)}
                                        placeholder="0"
                                    />
                                </div>
                            )}

                            {/* Render remaining tender types if any */}
                            {tenderTypes.length > 3 && tenderTypes.slice(3).map(tt => (
                                <div key={tt.tenderCode} className="flex items-center justify-between col-span-1">
                                    <span className="font-medium text-slate-600">{tt.name}</span>
                                    <input 
                                        type="number"
                                        className="w-24 px-2 py-0.5 text-right bg-white border border-slate-300 rounded focus:ring-1 focus:ring-indigo-500 outline-none h-7"
                                        value={tenderAmounts[tt.tenderCode] || ''}
                                        onChange={(e) => handlePaymentChange(tt.tenderCode, e.target.value)}
                                        placeholder="0"
                                    />
                                </div>
                            ))}

                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SalesEntry;
