import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Sales.css';

const Sales = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [currentItem, setCurrentItem] = useState({
    code: '',
    quantity: 1,
    mrp: '',
    amount: 0
  });

  const codeInputRef = useRef(null);
  const qtyInputRef = useRef(null);
  const mrpInputRef = useRef(null);

  // Calculate amount whenever quantity or mrp changes
  useEffect(() => {
    const qty = parseFloat(currentItem.quantity) || 0;
    const mrp = parseFloat(currentItem.mrp) || 0;
    setCurrentItem(prev => ({
      ...prev,
      amount: qty * mrp
    }));
  }, [currentItem.quantity, currentItem.mrp]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentItem(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddItem = (e) => {
    e.preventDefault();
    if (!currentItem.code || !currentItem.quantity || !currentItem.mrp) {
      alert('Please fill in all fields');
      return;
    }

    const newItem = {
      ...currentItem,
      id: Date.now(), // Temporary ID
      quantity: parseFloat(currentItem.quantity),
      mrp: parseFloat(currentItem.mrp)
    };

    setItems([...items, newItem]);

    // Reset form and focus back to code input for next scan
    setCurrentItem({
      code: '',
      quantity: 1,
      mrp: '',
      amount: 0
    });
    codeInputRef.current.focus();
  };

  const handleRemoveItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  const handleKeyDown = (e, nextRef) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      if (nextRef) {
        nextRef.current.focus();
      } else {
        handleAddItem(e);
      }
    }
  };

  const calculateTotal = () => {
    return items.reduce((sum, item) => sum + item.amount, 0);
  };

  return (
    <div className="sales-container">
      <div className="sales-header">
        <h2>New Sale</h2>
        <button className="back-button" onClick={() => navigate('/store-dashboard')}>
          Back to Dashboard
        </button>
      </div>

      <div className="sales-content">
        <div className="scan-section">
          <h3>Add Item</h3>
          <form onSubmit={handleAddItem}>
            <div className="scan-input-group">
              <label>Item Code / Scan</label>
              <input
                ref={codeInputRef}
                type="text"
                name="code"
                value={currentItem.code}
                onChange={handleInputChange}
                onKeyDown={(e) => handleKeyDown(e, qtyInputRef)}
                placeholder="Scan barcode or enter code"
                autoFocus
                autoComplete="off"
              />
            </div>

            <div className="scan-input-group">
              <label>Quantity</label>
              <input
                ref={qtyInputRef}
                type="number"
                name="quantity"
                value={currentItem.quantity}
                onChange={handleInputChange}
                onKeyDown={(e) => handleKeyDown(e, mrpInputRef)}
                min="1"
              />
            </div>

            <div className="scan-input-group">
              <label>MRP</label>
              <input
                ref={mrpInputRef}
                type="number"
                name="mrp"
                value={currentItem.mrp}
                onChange={handleInputChange}
                onKeyDown={(e) => handleKeyDown(e, null)} // null means submit
                placeholder="0.00"
                step="0.01"
              />
            </div>

            <div className="scan-input-group">
              <label>Amount</label>
              <input
                type="text"
                value={currentItem.amount.toFixed(2)}
                readOnly
                style={{ backgroundColor: '#f8f9fa' }}
              />
            </div>

            <button type="submit" className="add-btn">
              Add Item
            </button>
          </form>
        </div>

        <div className="cart-section">
          <h3>Cart ({items.length} items)</h3>

          <table className="cart-table">
            <thead>
              <tr>
                <th>Code</th>
                <th>Qty</th>
                <th>MRP</th>
                <th>Amount</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {items.length === 0 ? (
                <tr>
                  <td colSpan="5" style={{ textAlign: 'center', color: '#999' }}>
                    No items added yet. Scan an item to begin.
                  </td>
                </tr>
              ) : (
                items.map(item => (
                  <tr key={item.id}>
                    <td>{item.code}</td>
                    <td>{item.quantity}</td>
                    <td>{item.mrp.toFixed(2)}</td>
                    <td>{item.amount.toFixed(2)}</td>
                    <td>
                      <button 
                        className="delete-btn"
                        onClick={() => handleRemoveItem(item.id)}
                      >
                        &times;
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>

          <div className="cart-summary">
            <div className="total-amount">
              Total: {calculateTotal().toFixed(2)}
            </div>
            <button 
              className="checkout-btn"
              disabled={items.length === 0}
              onClick={() => alert('Checkout functionality coming soon!')}
            >
              Complete Sale
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sales;
