import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './PartyList.css';

const PartyList = () => {
  const navigate = useNavigate();
  const [parties, setParties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingParty, setEditingParty] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    partyCode: '',
    partyName: '',
    address: '',
    phone: ''
  });

  // Fetch parties from API
  const fetchParties = useCallback(async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/parties', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setParties(response.data.parties || []);
        setError('');
      } else {
        setError(response.data.message || 'Failed to fetch parties');
      }
    } catch (err) {
      console.error('Error fetching parties:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      } else {
        setError('Failed to fetch parties. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchParties();
  }, [fetchParties]);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear validation error for this field
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    if (!formData.partyCode.trim()) errors.partyCode = 'Party Code is required';
    if (!formData.partyName.trim()) errors.partyName = 'Party Name is required';

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const token = localStorage.getItem('token');
      const url = editingParty 
        ? `http://localhost:8080/api/parties/${editingParty.id}`
        : 'http://localhost:8080/api/parties';

      const method = editingParty ? 'put' : 'post';

      const response = await axios[method](url, formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        closeModal();
        fetchParties();
      } else {
        setModalError(response.data.message || 'Operation failed');
      }
    } catch (err) {
      console.error('Error saving party:', err);
      setModalError(err.response?.data?.message || 'Failed to save party. Please try again.');
    }
  };

  // Handle delete party
  const handleDelete = async (partyId) => {
    if (!window.confirm('Are you sure you want to delete this party?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await axios.delete(
        `http://localhost:8080/api/parties/${partyId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        fetchParties(); // Refresh the list
      } else {
        alert(response.data.message || 'Failed to delete party');
      }
    } catch (err) {
      console.error('Error deleting party:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      } else {
        alert(err.response?.data?.message || 'Failed to delete party. Please try again.');
      }
    }
  };

  // Handle edit party
  const handleEdit = (party) => {
    setEditingParty(party);
    setFormData({
      partyCode: party.partyCode,
      partyName: party.partyName,
      address: party.address || '',
      phone: party.phone || ''
    });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Handle add new party
  const handleAdd = () => {
    setEditingParty(null);
    setFormData({ partyCode: '', partyName: '', address: '', phone: '' });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
    setEditingParty(null);
    setFormData({ partyCode: '', partyName: '', address: '', phone: '' });
    setValidationErrors({});
    setModalError('');
  };

  if (loading) {
    return <div className="loading">Loading parties...</div>;
  }

  return (
    <div className="party-list-container">
      <div className="party-list-header">
        <div className="header-left">
          <button className="back-button" onClick={() => navigate('/dashboard')} title="Back to Dashboard">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="19" y1="12" x2="5" y2="12"></line>
              <polyline points="12 19 5 12 12 5"></polyline>
            </svg>
          </button>
          <h1>Party List</h1>
        </div>
        <div className="header-buttons">
          <button className="add-btn" onClick={handleAdd}>
            Add New Party
          </button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="table-container">
        <table className="party-table">
          <thead>
            <tr>
              <th>Code</th>
              <th>Name</th>
              <th>Phone</th>
              <th>Address</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {parties.length === 0 ? (
              <tr>
                <td colSpan="5" className="no-data">No parties found</td>
              </tr>
            ) : (
              parties.map((party) => (
                <tr key={party.id}>
                  <td>{party.partyCode}</td>
                  <td>{party.partyName}</td>
                  <td>{party.phone}</td>
                  <td>{party.address}</td>
                  <td className="actions">
                    <button 
                      className="edit-btn" 
                      onClick={() => handleEdit(party)}
                    >
                      Edit
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={() => handleDelete(party.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Modal for Add/Edit Party */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editingParty ? 'Edit Party' : 'Add New Party'}</h2>
              <button onClick={closeModal} className="close-btn">Ã—</button>
            </div>

            {modalError && (
              <div className="modal-error-message">
                {modalError}
              </div>
            )}

            <div className="party-form">
              <form onSubmit={handleSubmit}>
                <div className="form-grid">
                  <div className="form-group">
                    <label htmlFor="partyCode">Party Code *</label>
                    <input
                      type="text"
                      id="partyCode"
                      name="partyCode"
                      value={formData.partyCode}
                      onChange={handleInputChange}
                      className={validationErrors.partyCode ? 'error' : ''}
                      placeholder="Enter party code"
                      maxLength="20"
                    />
                    {validationErrors.partyCode && (
                      <span className="error-message">{validationErrors.partyCode}</span>
                    )}
                  </div>

                  <div className="form-group">
                    <label htmlFor="partyName">Party Name *</label>
                    <input
                      type="text"
                      id="partyName"
                      name="partyName"
                      value={formData.partyName}
                      onChange={handleInputChange}
                      className={validationErrors.partyName ? 'error' : ''}
                      placeholder="Enter party name"
                      maxLength="200"
                    />
                    {validationErrors.partyName && (
                      <span className="error-message">{validationErrors.partyName}</span>
                    )}
                  </div>

                  <div className="form-group">
                    <label htmlFor="phone">Phone</label>
                    <input
                      type="text"
                      id="phone"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Enter phone number"
                      maxLength="15"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="address">Address</label>
                    <textarea
                      id="address"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      placeholder="Enter address"
                      rows="3"
                    />
                  </div>
                </div>

                <div className="form-actions">
                  <button type="button" onClick={closeModal} className="cancel-btn">
                    Cancel
                  </button>
                  <button type="submit" className="save-btn">
                    {editingParty ? 'Update Party' : 'Add Party'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PartyList;
