import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './BrandList.css';

const BrandList = () => {
  const navigate = useNavigate();
  const [brands, setBrands] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingBrand, setEditingBrand] = useState(null);
  const [formData, setFormData] = useState({
    code: '',
    name: '',
    status: true
  });
  const [validationErrors, setValidationErrors] = useState({});
  const [modalError, setModalError] = useState('');

  // Validation functions
  const validateBrandCode = (code) => {
    if (!code || code.trim() === '') {
      return 'Brand code is required';
    }
    if (code.length < 2 || code.length > 50) {
      return 'Brand code must be between 2 and 50 characters';
    }
    if (!/^[A-Z0-9_-]+$/.test(code)) {
      return 'Brand code must contain only uppercase letters, numbers, underscores, and hyphens';
    }
    return '';
  };

  const validateBrandName = (name) => {
    if (!name || name.trim() === '') {
      return 'Brand name is required';
    }
    if (name.length < 2 || name.length > 200) {
      return 'Brand name must be between 2 and 200 characters';
    }
    return '';
  };

  const validateForm = () => {
    const errors = {};

    const codeError = validateBrandCode(formData.code);
    if (codeError) errors.code = codeError;

    const nameError = validateBrandName(formData.name);
    if (nameError) errors.name = nameError;

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Fetch brands from API
  const fetchBrands = useCallback(async () => {
    try {
      setLoading(true);
      setError('');

      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.get('http://localhost:8080/api/brands', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setBrands(response.data.brands || []);
      } else {
        setError(response.data.message || 'Failed to fetch brands');
      }
    } catch (err) {
      console.error('Error fetching brands:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError('Failed to fetch brands. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchBrands();
  }, [fetchBrands]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear validation error for this field
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Open modal for adding new brand
  const handleAddBrand = () => {
    setEditingBrand(null);
    setFormData({
      code: '',
      name: '',
      status: true
    });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Open modal for editing brand
  const handleEditBrand = (brand) => {
    setEditingBrand(brand);
    setFormData({
      code: brand.code,
      name: brand.name,
      status: brand.status
    });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Close modal
  const handleCloseModal = () => {
    setShowModal(false);
    setEditingBrand(null);
    setFormData({
      code: '',
      name: '',
      status: true
    });
    setValidationErrors({});
    setModalError('');
  };

  // Save brand (create or update)
  const handleSaveBrand = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      setModalError('');
      const token = localStorage.getItem('token');

      if (!token) {
        navigate('/login');
        return;
      }

      const brandData = {
        code: formData.code.trim().toUpperCase(),
        name: formData.name.trim(),
        status: formData.status
      };

      let response;
      if (editingBrand) {
        // Update existing brand
        response = await axios.put(
          `http://localhost:8080/api/brands/${editingBrand.id}`,
          brandData,
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );
      } else {
        // Create new brand
        response = await axios.post(
          'http://localhost:8080/api/brands',
          brandData,
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );
      }

      if (response.data.success) {
        handleCloseModal();
        fetchBrands(); // Refresh the list
      } else {
        setModalError(response.data.message || 'Failed to save brand');
      }
    } catch (err) {
      console.error('Error saving brand:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setModalError(err.response?.data?.message || 'Failed to save brand. Please try again.');
      }
    }
  };

  // Delete brand
  const handleDeleteBrand = async (brandId, brandName) => {
    if (!window.confirm(`Are you sure you want to delete the brand "${brandName}"?`)) {
      return;
    }

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        navigate('/login');
        return;
      }

      const response = await axios.delete(
        `http://localhost:8080/api/brands/${brandId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        fetchBrands(); // Refresh the list
      } else {
        setError(response.data.message || 'Failed to delete brand');
      }
    } catch (err) {
      console.error('Error deleting brand:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setError(err.response?.data?.message || 'Failed to delete brand. Please try again.');
      }
    }
  };

  return (
    <div className="brand-list-container">
      <div className="brand-list-header">
        <h1>Brand Management</h1>
        <div className="header-buttons">
          <button 
            className="dashboard-btn"
            onClick={() => navigate('/dashboard')}
          >
            Back to Dashboard
          </button>
          <button 
            className="add-btn"
            onClick={handleAddBrand}
          >
            Add Brand
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {loading ? (
        <div className="loading">Loading brands...</div>
      ) : (
        <div className="table-container">
          <table className="brand-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Code</th>
                <th>Name</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {brands.length === 0 ? (
                <tr>
                  <td colSpan="5" className="no-data">
                    No brands found
                  </td>
                </tr>
              ) : (
                brands.map((brand) => (
                  <tr key={brand.id}>
                    <td>{brand.id}</td>
                    <td>{brand.code}</td>
                    <td>{brand.name}</td>
                    <td>
                      <span className={`status ${brand.status ? 'active' : 'inactive'}`}>
                        {brand.status ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td>
                      <div className="actions">
                        <button
                          className="edit-btn"
                          onClick={() => handleEditBrand(brand)}
                        >
                          Edit
                        </button>
                        <button
                          className="delete-btn"
                          onClick={() => handleDeleteBrand(brand.id, brand.name)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}

      {/* Modal for Add/Edit Brand */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editingBrand ? 'Edit Brand' : 'Add New Brand'}</h2>
              <div className="header-actions">
                <button
                  className="save-btn"
                  onClick={handleSaveBrand}
                  disabled={Object.keys(validationErrors).length > 0}
                >
                  Save
                </button>
                <button
                  className="close-btn"
                  onClick={handleCloseModal}
                >
                  Ã—
                </button>
              </div>
            </div>

            {modalError && (
              <div className="modal-error-message">
                {modalError}
              </div>
            )}

            <form className="brand-form" onSubmit={(e) => e.preventDefault()}>
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="code">Brand Code *</label>
                  <input
                    type="text"
                    id="code"
                    name="code"
                    value={formData.code}
                    onChange={handleInputChange}
                    className={validationErrors.code ? 'error' : ''}
                    disabled={editingBrand !== null}
                    required
                  />
                  {validationErrors.code && (
                    <span className="error-text">{validationErrors.code}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="name">Brand Name *</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className={validationErrors.name ? 'error' : ''}
                    required
                  />
                  {validationErrors.name && (
                    <span className="error-text">{validationErrors.name}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="status">Status</label>
                  <select
                    id="status"
                    name="status"
                    value={formData.status}
                    onChange={handleInputChange}
                  >
                    <option value={true}>Active</option>
                    <option value={false}>Inactive</option>
                  </select>
                </div>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default BrandList;
