import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import './SalesEntry.css';

const SalesEntry = () => {
    // --- State ---
    // Header
    const [parties, setParties] = useState([]);
    const [selectedParty, setSelectedParty] = useState('');
    const [invoiceDate, setInvoiceDate] = useState(new Date().toISOString().split('T')[0]);
    const [invoiceNo, setInvoiceNo] = useState('New');
    const [storeInfo, setStoreInfo] = useState(null);

    // Item Input
    const [itemCode, setItemCode] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [currentItem, setCurrentItem] = useState(null); // { itemCode, itemName, mrp }
    const [itemError, setItemError] = useState('');

    // Body
    const [items, setItems] = useState([]); // Array of { itemCode, mrp, quantity, amount }

    // Footer
    const [tenderType, setTenderType] = useState('Cash');
    const [totalAmount, setTotalAmount] = useState(0);

    // Refs for focus management
    const quantityInputRef = useRef(null);
    const itemCodeInputRef = useRef(null);

    // --- Helpers ---
    const calculateTotal = useCallback(() => {
        const total = items.reduce((sum, item) => sum + item.amount, 0);
        setTotalAmount(total);
    }, [items]);

    // --- Effects ---
    useEffect(() => {
        fetchParties();
        fetchNextInvoiceNo();
        fetchStoreInfo();
    }, []);

    useEffect(() => {
        calculateTotal();
    }, [calculateTotal]);

    // --- API Calls ---
    const fetchParties = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/parties');
            setParties(response.data);
        } catch (error) {
            console.error("Error fetching parties", error);
        }
    };

    const fetchNextInvoiceNo = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/sales/generate-invoice-no');
            setInvoiceNo(response.data);
        } catch (error) {
            console.error("Error fetching invoice no", error);
        }
    };

    const fetchItem = async (code) => {
        if (!code) return;
        setItemError('');
        try {
            const response = await axios.get(`http://localhost:8080/api/sales/items/${code}`);
            setCurrentItem(response.data);
            // Auto focus quantity after successful scan
            if (quantityInputRef.current) {
                quantityInputRef.current.focus();
                quantityInputRef.current.select();
            }
        } catch (error) {
            setCurrentItem(null);
            setItemError('Item not found');
        }
    };

    // --- Handlers ---
    const handleItemCodeKeyDown = (e) => {
        if (e.key === 'Enter') {
            fetchItem(itemCode);
        }
    };

    const handleQuantityKeyDown = (e) => {
        if (e.key === 'Enter') {
            addItemToBody();
        }
    };

    const addItemToBody = () => {
        if (!currentItem) {
            setItemError('Please scan a valid item first');
            return;
        }
        if (quantity <= 0) {
            setItemError('Quantity must be greater than 0');
            return;
        }

        const newItem = {
            itemCode: currentItem.itemCode,
            itemName: currentItem.itemName, // storing name for display if needed
            mrp: currentItem.mrp,
            quantity: parseInt(quantity),
            amount: currentItem.mrp * parseInt(quantity)
        };

        setItems(prevItems => {
            const existingItemIndex = prevItems.findIndex(item => item.itemCode === newItem.itemCode);
            if (existingItemIndex >= 0) {
                // Update existing
                const updatedItems = [...prevItems];
                const existingItem = updatedItems[existingItemIndex];
                const newQty = existingItem.quantity + newItem.quantity;
                updatedItems[existingItemIndex] = {
                    ...existingItem,
                    quantity: newQty,
                    amount: newQty * existingItem.mrp
                };
                return updatedItems;
            } else {
                // Add new
                return [...prevItems, newItem];
            }
        });

        // Reset inputs
        setItemCode('');
        setQuantity(1);
        setCurrentItem(null);
        setItemError('');
        // Focus back to item code for next scan
        if (itemCodeInputRef.current) {
            itemCodeInputRef.current.focus();
        }
    };

    const handleEditItem = (index, newQty) => {
        const qty = parseInt(newQty);
        if (isNaN(qty) || qty < 0) return;

        setItems(prevItems => {
            const updatedItems = [...prevItems];
            updatedItems[index] = {
                ...updatedItems[index],
                quantity: qty,
                amount: qty * updatedItems[index].mrp
            };
            return updatedItems;
        });
    };

    const handleDeleteItem = (index) => {
        setItems(prevItems => prevItems.filter((_, i) => i !== index));
    };

    // calculateTotal moved to useCallback above

    const handleSave = async () => {
        // Validation
        if (!selectedParty) {
            alert('Please select a Party Name');
            return;
        }
        if (items.length === 0) {
            alert('Please add at least one item');
            return;
        }
        if (!tenderType) {
            alert('Please select a Tender Type');
            return;
        }

        const payload = {
            invoiceNo,
            invoiceDate,
            partyCode: selectedParty,
            totalAmount,
            tenderType,
            items: items.map(item => ({
                itemCode: item.itemCode,
                mrp: item.mrp,
                quantity: item.quantity,
                amount: item.amount
            }))
        };

        try {
            await axios.post('http://localhost:8080/api/sales/save', payload);
            alert('Invoice saved successfully');
            resetForm();
        } catch (error) {
            console.error("Error saving invoice", error);
            alert('Failed to save invoice');
        }
    };

    const resetForm = () => {
        setItems([]);
        setSelectedParty('');
        setTenderType('Cash');
        setInvoiceDate(new Date().toISOString().split('T')[0]);
        setItemCode('');
        setQuantity(1);
        setCurrentItem(null);
        fetchNextInvoiceNo();
    };

    const handleCancel = () => {
        if (window.confirm('Do you want to cancel this invoice?')) {
            resetForm();
        }
    };

    return (
        <div className="sales-container">
            <h2 className="sales-title">Sales Voucher</h2>
            {/* Header Section */}
            <div className="sales-header">
                <div className="header-row">
                    <div className="form-group" style={{flex: 2}}>
                        <label>Party Name</label>
                        <select 
                            className="form-control"
                            value={selectedParty}
                            onChange={(e) => setSelectedParty(e.target.value)}
                        >
                            <option value="">Select Party</option>
                            {parties.map(party => (
                                <option key={party.id} value={party.partyCode}>
                                    {party.partyName}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group" style={{flex: 1}}>
                        <label>Invoice Date</label>
                        <input 
                            type="date" 
                            className="form-control"
                            value={invoiceDate}
                            onChange={(e) => setInvoiceDate(e.target.value)}
                        />
                    </div>
                    <div className="form-group" style={{flex: 1}}>
                        <label>Invoice Number</label>
                        <input 
                            type="text" 
                            className="form-control"
                            value={invoiceNo}
                            readOnly
                        />
                    </div>
                </div>

                <div className="header-row">
                    <div className="form-group" style={{flex: 1}}>
                        <label>Item Code (Scan/Enter)</label>
                        <input 
                            ref={itemCodeInputRef}
                            type="text" 
                            className="form-control"
                            value={itemCode}
                            onChange={(e) => setItemCode(e.target.value)}
                            onKeyDown={handleItemCodeKeyDown}
                            onBlur={() => fetchItem(itemCode)}
                            placeholder="Scan or Enter Item Code"
                        />
                        {itemError && <span className="error-msg">{itemError}</span>}
                    </div>
                </div>

                <div className="header-row">
                    <div className="form-group" style={{flex: 1}}>
                        <label>Quantity</label>
                        <input 
                            ref={quantityInputRef}
                            type="number" 
                            className="form-control"
                            value={quantity}
                            onChange={(e) => setQuantity(e.target.value)}
                            onKeyDown={handleQuantityKeyDown}
                            min="1"
                        />
                    </div>
                    <div className="form-group" style={{flex: 1}}>
                        <label>MRP</label>
                        <input 
                            type="text" 
                            className="form-control"
                            value={currentItem ? currentItem.mrp.toFixed(2) : ''}
                            readOnly
                        />
                    </div>
                    <div className="form-group">
                        <button className="btn-primary" onClick={addItemToBody}>
                            Add
                        </button>
                    </div>
                </div>
            </div>

            {/* Body Section */}
            <div className="sales-body">
                <table className="sales-table">
                    <thead>
                        <tr>
                            <th>Item Code</th>
                            <th>MRP</th>
                            <th>Quantity</th>
                            <th>Amount</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item, index) => (
                            <tr key={index}>
                                <td>{item.itemCode}</td>
                                <td>{item.mrp.toFixed(2)}</td>
                                <td>
                                    <input 
                                        type="number"
                                        className="form-control"
                                        style={{width: '80px', padding: '4px'}}
                                        value={item.quantity}
                                        onChange={(e) => handleEditItem(index, e.target.value)}
                                        min="1"
                                    />
                                </td>
                                <td>{item.amount.toFixed(2)}</td>
                                <td>
                                    <button 
                                        className="btn-danger btn-sm"
                                        onClick={() => handleDeleteItem(index)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                        {items.length === 0 && (
                            <tr>
                                <td colSpan="5" style={{textAlign: 'center', color: '#888'}}>
                                    No items added. Scan or enter item code to begin.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>

            {/* Footer Section */}
            <div className="sales-footer">
                <div className="footer-left">
                    <div className="form-group">
                        <label>Total Amount</label>
                        <span className="total-amount">â‚¹ {totalAmount.toFixed(2)}</span>
                    </div>
                    <div className="form-group">
                        <label>Tender Type</label>
                        <div className="tender-type-group">
                            <label className="radio-label">
                                <input 
                                    type="radio" 
                                    value="Cash" 
                                    checked={tenderType === 'Cash'}
                                    onChange={(e) => setTenderType(e.target.value)}
                                /> Cash
                            </label>
                            <label className="radio-label">
                                <input 
                                    type="radio" 
                                    value="Card" 
                                    checked={tenderType === 'Card'}
                                    onChange={(e) => setTenderType(e.target.value)}
                                /> Card
                            </label>
                            <label className="radio-label">
                                <input 
                                    type="radio" 
                                    value="Credit" 
                                    checked={tenderType === 'Credit'}
                                    onChange={(e) => setTenderType(e.target.value)}
                                /> Credit
                            </label>
                        </div>
                    </div>
                </div>
                <div className="footer-right">
                    <button className="btn-primary" onClick={handleSave}>Save</button>
                    <button className="btn-secondary" onClick={handleCancel}>Cancel</button>
                </div>
            </div>
        </div>
    );
};

export default SalesEntry;
