import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './SizeList.css';

const SizeList = () => {
  const navigate = useNavigate();
  const [sizes, setSizes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingSize, setEditingSize] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    code: '',
    name: '',
    status: true
  });

  // Validation functions
  const validateCode = (code) => {
    const codeRegex = /^[A-Z0-9]{2,10}$/; // 2-10 characters, uppercase letters and numbers only
    return codeRegex.test(code);
  };

  // Validate form fields
  const validateForm = () => {
    const errors = {};

    // Required field validations
    if (!formData.code.trim()) {
      errors.code = 'Size code is required';
    } else if (!validateCode(formData.code)) {
      errors.code = 'Size code must be 2-10 uppercase letters/numbers';
    }

    if (!formData.name.trim()) {
      errors.name = 'Size name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Size name must be at least 2 characters';
    } else if (formData.name.trim().length > 200) {
      errors.name = 'Size name must not exceed 200 characters';
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Fetch sizes from API
  const fetchSizes = useCallback(async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/sizes', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        setSizes(response.data.sizes || []);
        setError('');
      } else {
        setError(response.data.message || 'Failed to fetch sizes');
      }
    } catch (err) {
      console.error('Error fetching sizes:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      } else {
        setError('Failed to fetch sizes. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear validation error for this field
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      };

      let response;
      if (editingSize) {
        // Update existing size
        response = await axios.put(
          `http://localhost:8080/api/sizes/${editingSize.id}`,
          formData,
          config
        );
      } else {
        // Create new size
        response = await axios.post(
          'http://localhost:8080/api/sizes',
          formData,
          config
        );
      }

      if (response.data.success) {
        setShowModal(false);
        setEditingSize(null);
        setFormData({ code: '', name: '', status: true });
        setValidationErrors({});
        setModalError('');
        fetchSizes(); // Refresh the list
      } else {
        setModalError(response.data.message || 'Operation failed');
      }
    } catch (err) {
      console.error('Error saving size:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      } else {
        setModalError(err.response?.data?.message || 'Failed to save size. Please try again.');
      }
    }
  };

  // Handle delete size
  const handleDelete = async (sizeId) => {
    if (!window.confirm('Are you sure you want to delete this size?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await axios.delete(
        `http://localhost:8080/api/sizes/${sizeId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        fetchSizes(); // Refresh the list
      } else {
        alert(response.data.message || 'Failed to delete size');
      }
    } catch (err) {
      console.error('Error deleting size:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
      } else {
        alert(err.response?.data?.message || 'Failed to delete size. Please try again.');
      }
    }
  };

  // Handle edit size
  const handleEdit = (size) => {
    setEditingSize(size);
    setFormData({
      code: size.code,
      name: size.name,
      status: size.status
    });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Handle add new size
  const handleAdd = () => {
    setEditingSize(null);
    setFormData({ code: '', name: '', status: true });
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
    setEditingSize(null);
    setFormData({ code: '', name: '', status: true });
    setValidationErrors({});
    setModalError('');
  };

  useEffect(() => {
    fetchSizes();
  }, [fetchSizes]);

  if (loading) {
    return <div className="size-list-loading">Loading sizes...</div>;
  }

  return (
    <div className="size-list-container">
      <div className="size-list-header">
        <h2>Size Management</h2>
        <button className="add-size-btn" onClick={handleAddSize}>
          Add New Size
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="size-list-table-container">
        <table className="size-list-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Code</th>
              <th>Name</th>
              <th>Status</th>
              <th>Created At</th>
              <th>Updated At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sizes.length === 0 ? (
              <tr>
                <td colSpan="7" className="no-data">No sizes found</td>
              </tr>
            ) : (
              sizes.map(size => (
                <tr key={size.id}>
                  <td>{size.id}</td>
                  <td>{size.code}</td>
                  <td>{size.name}</td>
                  <td>
                    <span className={`status-badge ${size.status.toLowerCase()}`}>
                      {size.status}
                    </span>
                  </td>
                  <td>{new Date(size.createdAt).toLocaleDateString()}</td>
                  <td>{new Date(size.updatedAt).toLocaleDateString()}</td>
                  <td className="actions">
                    <button 
                      className="edit-btn" 
                      onClick={() => handleEditSize(size)}
                    >
                      Edit
                    </button>
                    <button 
                      className={`status-btn ${size.status.toLowerCase()}`}
                      onClick={() => handleToggleStatus(size.id, size.status)}
                    >
                      {size.status === 'ACTIVE' ? 'Deactivate' : 'Activate'}
                    </button>
                    <button 
                      className="delete-btn" 
                      onClick={() => handleDeleteSize(size.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>{editingSize ? 'Edit Size' : 'Add New Size'}</h3>
              <button 
                className="close-btn" 
                onClick={() => setShowModal(false)}
              >
                Ã—
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="code">Size Code *</label>
                <input
                  type="text"
                  id="code"
                  name="code"
                  value={formData.code}
                  onChange={handleInputChange}
                  className={validationErrors.code ? 'error' : ''}
                  placeholder="Enter size code (e.g., XS, S, M, L, XL)"
                />
                {validationErrors.code && (
                  <span className="field-error">{validationErrors.code}</span>
                )}
              </div>
              <div className="form-group">
                <label htmlFor="name">Size Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={validationErrors.name ? 'error' : ''}
                  placeholder="Enter size name"
                />
                {validationErrors.name && (
                  <span className="field-error">{validationErrors.name}</span>
                )}
              </div>
              <div className="form-group">
                <label htmlFor="status">Status</label>
                <select
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                >
                  <option value="ACTIVE">Active</option>
                  <option value="INACTIVE">Inactive</option>
                </select>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="cancel-btn" 
                onClick={() => setShowModal(false)}
              >
                Cancel
              </button>
              <button 
                className="save-btn" 
                onClick={handleSaveSize}
              >
                {editingSize ? 'Update' : 'Save'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SizeList;
