import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ItemList.css';

const ItemList = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [sizes, setSizes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    itemCode: '',
    itemName: '',
    categoryCode: '',
    brandCode: '',
    size: '',
    purPrice: '',
    salePrice: '',
    status: true
  });

  const fetchItems = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/items');
      if (response.data.success) {
        setItems(response.data.items || []);
        setError('');
      } else {
        setItems([]);
        setError(response.data.message || 'Failed to fetch items');
      }
    } catch (err) {
      console.error('Error fetching items:', err);
      setError('Failed to fetch items. Please try again.');
      setItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchCategories = useCallback(async () => {
    try {
      const response = await axios.get('/api/categories');
      if (response.data.success) {
        setCategories(response.data.categories || []);
      }
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  }, []);

  const fetchBrands = useCallback(async () => {
    try {
      const response = await axios.get('/api/brands');
      if (response.data.success) {
        setBrands(response.data.brands || []);
      }
    } catch (err) {
      console.error('Error fetching brands:', err);
    }
  }, []);

  const fetchSizes = useCallback(async () => {
    try {
      const response = await axios.get('/api/sizes');
      if (response.data.success) {
        setSizes(response.data.sizes || []);
      }
    } catch (err) {
      console.error('Error fetching sizes:', err);
    }
  }, []);

  useEffect(() => {
    fetchItems();
    fetchCategories();
    fetchBrands();
    fetchSizes();
  }, [fetchItems, fetchCategories, fetchBrands, fetchSizes]);

  const handleOpenModal = (item = null) => {
    setEditingItem(item);
    if (item) {
      setFormData({
        itemCode: item.itemCode || '',
        itemName: item.itemName || '',
        categoryCode: item.categoryCode || '',
        brandCode: item.brandCode || '',
        size: item.size || '',
        purPrice: item.purPrice || '',
        salePrice: item.salePrice || '',
        status: item.status !== undefined ? item.status : true
      });
    } else {
      setFormData({
        itemCode: '',
        itemName: '',
        categoryCode: '',
        brandCode: '',
        size: '',
        purPrice: '',
        salePrice: '',
        status: true
      });
    }
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingItem(null);
    setValidationErrors({});
    setModalError('');
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.itemCode.trim()) errors.itemCode = 'Item code is required';
    if (!formData.itemName.trim()) errors.itemName = 'Item name is required';
    if (!formData.categoryCode) errors.categoryCode = 'Category is required';
    if (!formData.brandCode) errors.brandCode = 'Brand is required';
    if (!formData.size) errors.size = 'Size is required';
    if (formData.purPrice === '' || Number(formData.purPrice) < 0) errors.purPrice = 'Purchase price is required';
    if (formData.salePrice === '' || Number(formData.salePrice) < 0) errors.salePrice = 'Sale price is required';
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      const payload = {
        itemCode: formData.itemCode.trim().toUpperCase(),
        itemName: formData.itemName.trim(),
        categoryCode: formData.categoryCode,
        brandCode: formData.brandCode,
        size: formData.size,
        purPrice: Number(formData.purPrice),
        salePrice: Number(formData.salePrice),
        status: formData.status
      };

      let response;
      if (editingItem) {
        response = await axios.put(`/api/items/${editingItem.id}`, payload);
      } else {
        response = await axios.post('/api/items', payload);
      }

      if (response.data.success) {
        handleCloseModal();
        fetchItems();
      } else {
        setModalError(response.data.message || 'Failed to save item');
      }
    } catch (err) {
      console.error('Error saving item:', err);
      setModalError(err.response?.data?.message || 'Failed to save item. Please try again.');
    }
  };

  const handleDelete = async (itemId) => {
    if (!window.confirm('Are you sure you want to delete this item?')) return;
    try {
      const response = await axios.delete(`/api/items/${itemId}`);
      if (response.data.success) {
        fetchItems();
      } else {
        alert(response.data.message || 'Failed to delete item');
      }
    } catch (err) {
      console.error('Error deleting item:', err);
      alert(err.response?.data?.message || 'Failed to delete item. Please try again.');
    }
  };

  return (
    <div className="item-list-container">
      <div className="item-list-header">
        <h1>Item List</h1>
        <div className="header-buttons">
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>Dashboard</button>
          <button className="add-btn" onClick={() => handleOpenModal()}>Add New Item</button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      {loading ? (
        <div className="loading">Loading items...</div>
      ) : (
        <div className="table-container">
          <table className="item-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Item Code</th>
                <th>Item Name</th>
                <th>Category</th>
                <th>Brand</th>
                <th>Size</th>
                <th>Purchase Price</th>
                <th>Sale Price</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.length === 0 ? (
                <tr>
                  <td colSpan="10" className="no-data">No items found</td>
                </tr>
              ) : (
                items.map(item => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.itemCode}</td>
                    <td>{item.itemName}</td>
                    <td>{categories.find(c => c.code === item.categoryCode)?.name || item.categoryCode}</td>
                    <td>{brands.find(b => b.code === item.brandCode)?.name || item.brandCode}</td>
                    <td>{item.size}</td>
                    <td>{item.purPrice}</td>
                    <td>{item.salePrice}</td>
                    <td>
                      <span className={`status ${item.status ? 'active' : 'inactive'}`}>
                        {item.status ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td className="actions">
                      <button className="edit-btn" onClick={() => handleOpenModal(item)}>Edit</button>
                      <button className="delete-btn" onClick={() => handleDelete(item.id)}>Delete</button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{editingItem ? 'Edit Item' : 'Add New Item'}</h2>
              <div className="header-actions">
                <button type="submit" className="save-btn" form="item-form">
                  Save
                </button>
                <button className="close-btn" onClick={handleCloseModal}>Ã—</button>
              </div>
            </div>
            {modalError && <div className="modal-error-message">{modalError}</div>}
            <form onSubmit={handleSubmit} className="item-form" id="item-form">
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="itemCode">Item Code *</label>
                  <input
                    type="text"
                    id="itemCode"
                    name="itemCode"
                    value={formData.itemCode}
                    onChange={handleChange}
                    className={validationErrors.itemCode ? 'error' : ''}
                    disabled={editingItem !== null}
                    required
                  />
                  {validationErrors.itemCode && (
                    <span className="error-text">{validationErrors.itemCode}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="itemName">Item Name *</label>
                  <input
                    type="text"
                    id="itemName"
                    name="itemName"
                    value={formData.itemName}
                    onChange={handleChange}
                    className={validationErrors.itemName ? 'error' : ''}
                    required
                  />
                  {validationErrors.itemName && (
                    <span className="error-text">{validationErrors.itemName}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="categoryCode">Category *</label>
                  <select
                    id="categoryCode"
                    name="categoryCode"
                    value={formData.categoryCode}
                    onChange={handleChange}
                    className={validationErrors.categoryCode ? 'error' : ''}
                    required
                  >
                    <option value="">Select Category</option>
                    {(editingItem ? categories : categories.filter(c => c.status)).map(c => (
                      <option key={c.id} value={c.code}>{c.name}</option>
                    ))}
                  </select>
                  {validationErrors.categoryCode && (
                    <span className="error-text">{validationErrors.categoryCode}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="brandCode">Brand *</label>
                  <select
                    id="brandCode"
                    name="brandCode"
                    value={formData.brandCode}
                    onChange={handleChange}
                    className={validationErrors.brandCode ? 'error' : ''}
                    required
                  >
                    <option value="">Select Brand</option>
                    {(editingItem ? brands : brands.filter(b => b.status)).map(b => (
                      <option key={b.id} value={b.code}>{b.name}</option>
                    ))}
                  </select>
                  {validationErrors.brandCode && (
                    <span className="error-text">{validationErrors.brandCode}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="size">Size *</label>
                  <select
                    id="size"
                    name="size"
                    value={formData.size}
                    onChange={handleChange}
                    className={validationErrors.size ? 'error' : ''}
                    required
                  >
                    <option value="">Select Size</option>
                    {(editingItem ? sizes : sizes.filter(s => s.status)).map(s => (
                      <option key={s.id} value={s.name}>{s.name}</option>
                    ))}
                  </select>
                  {validationErrors.size && (
                    <span className="error-text">{validationErrors.size}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="purPrice">Purchase Price *</label>
                  <input
                    type="number"
                    id="purPrice"
                    name="purPrice"
                    value={formData.purPrice}
                    onChange={handleChange}
                    className={validationErrors.purPrice ? 'error' : ''}
                    min="0"
                    required
                  />
                  {validationErrors.purPrice && (
                    <span className="error-text">{validationErrors.purPrice}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="salePrice">Sale Price *</label>
                  <input
                    type="number"
                    id="salePrice"
                    name="salePrice"
                    value={formData.salePrice}
                    onChange={handleChange}
                    className={validationErrors.salePrice ? 'error' : ''}
                    min="0"
                    required
                  />
                  {validationErrors.salePrice && (
                    <span className="error-text">{validationErrors.salePrice}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="status">Status</label>
                  <select
                    id="status"
                    name="status"
                    value={formData.status}
                    onChange={handleChange}
                  >
                    <option value={true}>Active</option>
                    <option value={false}>Inactive</option>
                  </select>
                </div>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ItemList;
