import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StoreDashboard.css';

const StoreDashboard = ({ setIsAuthenticated }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user') || '{}'));
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStores = async () => {
      try {
        if (!user.userName) {
          setError('User information not found. Please log in again.');
          setLoading(false);
          return;
        }

        const response = await axios.get(`/api/stores/by-user/${user.userName}`);
        if (response.data.success) {
          setStores(response.data.stores || []);
        } else {
          setError(response.data.message || 'Failed to fetch stores');
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching stores:', err);
        setError('Failed to load store information. Please try again later.');
        setLoading(false);
      }
    };

    fetchStores();
  }, [user.userName]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    navigate('/login');
  };

  if (loading) {
    return <div className="loading-container">Loading store information...</div>;
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Store Dashboard</h1>
          <div className="user-info">
            <span>Welcome, {user.userName || 'User'}!</span>
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-content">
          <div className="welcome-card">
            <h2>Store Management Portal</h2>

            {stores.length > 0 && (
              <div className="portal-header-store-info">
                 <div className="info-item">
                    <span className="label">Store Code</span>
                    <span className="value">{stores[0].storeCode}</span>
                 </div>
                 <div className="info-item">
                    <span className="label">Store Name</span>
                    <span className="value">{stores[0].storeName}</span>
                 </div>
                 <div className="info-item">
                    <span className="label">Address</span>
                    <span className="value">{stores[0].address || 'N/A'}</span>
                 </div>
              </div>
            )}

            <p>Here is your assigned store information.</p>

            {error && <div className="error-message">{error}</div>}
          </div>

          <div className="store-details-container">
            {stores.length > 0 ? (
              stores.map((store) => (
                <div key={store.id} className="store-card">
                  <h3>{store.storeName}</h3>

                  <div className="store-info-row">
                    <span className="store-info-label">Store Code:</span>
                    <span className="store-info-value">{store.storeCode}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Address:</span>
                    <span className="store-info-value">{store.address || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">City:</span>
                    <span className="store-info-value">{store.city || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Area:</span>
                    <span className="store-info-value">{store.area || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Zone:</span>
                    <span className="store-info-value">{store.zone || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Phone:</span>
                    <span className="store-info-value">{store.phone || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Email:</span>
                    <span className="store-info-value">{store.email || 'N/A'}</span>
                  </div>

                  <div className="store-info-row">
                    <span className="store-info-label">Status:</span>
                    <span className="store-info-value" style={{ 
                      color: store.status === 'Active' ? '#2ecc71' : '#e74c3c',
                      fontWeight: 'bold'
                    }}>
                      {store.status || 'Active'}
                    </span>
                  </div>
                </div>
              ))
            ) : (
              !error && (
                <div className="welcome-card">
                   <p>No stores are currently mapped to your account. Please contact the administrator.</p>
                </div>
              )
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default StoreDashboard;
