import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ItemList.css';

const ItemList = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [sizes, setSizes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalError, setModalError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});
  const [formData, setFormData] = useState({
    itemCode: '',
    itemName: '',
    categoryCode: '',
    brandCode: '',
    size: '',
    purPrice: '',
    salePrice: '',
    status: true
  });

  const fetchItems = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/items');
      if (response.data.success) {
        setItems(response.data.items || []);
        setError('');
      } else {
        setItems([]);
        setError(response.data.message || 'Failed to fetch items');
      }
    } catch (err) {
      console.error('Error fetching items:', err);
      setError('Failed to fetch items. Please try again.');
      setItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchCategories = useCallback(async () => {
    try {
      const response = await axios.get('/api/categories');
      if (response.data.success) {
        setCategories(response.data.categories || []);
      }
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  }, []);

  const fetchBrands = useCallback(async () => {
    try {
      const response = await axios.get('/api/brands');
      if (response.data.success) {
        setBrands(response.data.brands || []);
      }
    } catch (err) {
      console.error('Error fetching brands:', err);
    }
  }, []);

  const fetchSizes = useCallback(async () => {
    try {
      const response = await axios.get('/api/sizes');
      if (response.data.success) {
        setSizes(response.data.sizes || []);
      }
    } catch (err) {
      console.error('Error fetching sizes:', err);
    }
  }, []);

  useEffect(() => {
    fetchItems();
    fetchCategories();
    fetchBrands();
    fetchSizes();
  }, [fetchItems, fetchCategories, fetchBrands, fetchSizes]);

  const handleOpenModal = (item = null) => {
    setEditingItem(item);
    if (item) {
      setFormData({
        itemCode: item.itemCode || '',
        itemName: item.itemName || '',
        categoryCode: item.categoryCode || '',
        brandCode: item.brandCode || '',
        size: item.size || '',
        purPrice: item.purPrice || '',
        salePrice: item.salePrice || '',
        status: item.status !== undefined ? item.status : true
      });
    } else {
      setFormData({
        itemCode: '',
        itemName: '',
        categoryCode: '',
        brandCode: '',
        size: '',
        purPrice: '',
        salePrice: '',
        status: true
      });
    }
    setValidationErrors({});
    setModalError('');
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingItem(null);
    setValidationErrors({});
    setModalError('');
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.itemCode.trim()) errors.itemCode = 'Item code is required';
    if (!formData.itemName.trim()) errors.itemName = 'Item name is required';
    if (!formData.categoryCode) errors.categoryCode = 'Category is required';
    if (!formData.brandCode) errors.brandCode = 'Brand is required';
    if (!formData.size) errors.size = 'Size is required';
    if (formData.purPrice === '' || Number(formData.purPrice) < 0) errors.purPrice = 'Purchase price is required';
    if (formData.salePrice === '' || Number(formData.salePrice) < 0) errors.salePrice = 'Sale price is required';
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      const payload = {
        itemCode: formData.itemCode.trim().toUpperCase(),
        itemName: formData.itemName.trim(),
        categoryCode: formData.categoryCode,
        brandCode: formData.brandCode,
        size: formData.size,
        purPrice: Number(formData.purPrice),
        salePrice: Number(formData.salePrice),
        status: formData.status
      };

      let response;
      if (editingItem) {
        response = await axios.put(`/api/items/${editingItem.id}`, payload);
      } else {
        response = await axios.post('/api/items', payload);
      }

      if (response.data.success) {
        handleCloseModal();
        fetchItems();
      } else {
        setModalError(response.data.message || 'Failed to save item');
      }
    } catch (err) {
      console.error('Error saving item:', err);
      setModalError(err.response?.data?.message || 'Failed to save item. Please try again.');
    }
  };

  const handleDelete = async (itemId) => {
    if (!window.confirm('Are you sure you want to delete this item?')) return;
    try {
      const response = await axios.delete(`/api/items/${itemId}`);
      if (response.data.success) {
        fetchItems();
      } else {
        alert(response.data.message || 'Failed to delete item');
      }
    } catch (err) {
      console.error('Error deleting item:', err);
      alert(err.response?.data?.message || 'Failed to delete item. Please try again.');
    }
  };

  return (
    <div className="item-list">
      <div className="header">
        <h2>Item Master</h2>
        <button onClick={() => handleOpenModal()}>Add Item</button>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : (
        <table className="data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Item Code</th>
              <th>Item Name</th>
              <th>Category</th>
              <th>Brand</th>
              <th>Size</th>
              <th>Purchase Price</th>
              <th>Sale Price</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.itemCode}</td>
                <td>{item.itemName}</td>
                <td>{categories.find(c => c.code === item.categoryCode)?.name || item.categoryCode}</td>
                <td>{brands.find(b => b.code === item.brandCode)?.name || item.brandCode}</td>
                <td>{item.size}</td>
                <td>{item.purPrice}</td>
                <td>{item.salePrice}</td>
                <td>{item.status ? 'Active' : 'Inactive'}</td>
                <td>
                  <button onClick={() => handleOpenModal(item)}>Edit</button>
                  <button onClick={() => handleDelete(item.id)} className="danger">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>{editingItem ? 'Edit Item' : 'Add Item'}</h3>
            {modalError && <p className="error">{modalError}</p>}
            <form onSubmit={handleSubmit}>
              <div className="form-grid">
                <div className="form-group">
                  <label>Item Code</label>
                  <input name="itemCode" value={formData.itemCode} onChange={handleChange} />
                  {validationErrors.itemCode && <span className="field-error">{validationErrors.itemCode}</span>}
                </div>
                <div className="form-group">
                  <label>Item Name</label>
                  <input name="itemName" value={formData.itemName} onChange={handleChange} />
                  {validationErrors.itemName && <span className="field-error">{validationErrors.itemName}</span>}
                </div>
                <div className="form-group">
                  <label>Category</label>
                  <select name="categoryCode" value={formData.categoryCode} onChange={handleChange}>
                    <option value="">Select Category</option>
                    {categories.map(c => (
                      <option key={c.id} value={c.code}>{c.name}</option>
                    ))}
                  </select>
                  {validationErrors.categoryCode && <span className="field-error">{validationErrors.categoryCode}</span>}
                </div>
                <div className="form-group">
                  <label>Brand</label>
                  <select name="brandCode" value={formData.brandCode} onChange={handleChange}>
                    <option value="">Select Brand</option>
                    {brands.map(b => (
                      <option key={b.id} value={b.code}>{b.name}</option>
                    ))}
                  </select>
                  {validationErrors.brandCode && <span className="field-error">{validationErrors.brandCode}</span>}
                </div>
                <div className="form-group">
                  <label>Size</label>
                  <select name="size" value={formData.size} onChange={handleChange}>
                    <option value="">Select Size</option>
                    {sizes.map(s => (
                      <option key={s.id} value={s.name}>{s.name}</option>
                    ))}
                  </select>
                  {validationErrors.size && <span className="field-error">{validationErrors.size}</span>}
                </div>
                <div className="form-group">
                  <label>Purchase Price</label>
                  <input type="number" name="purPrice" value={formData.purPrice} onChange={handleChange} />
                  {validationErrors.purPrice && <span className="field-error">{validationErrors.purPrice}</span>}
                </div>
                <div className="form-group">
                  <label>Sale Price</label>
                  <input type="number" name="salePrice" value={formData.salePrice} onChange={handleChange} />
                  {validationErrors.salePrice && <span className="field-error">{validationErrors.salePrice}</span>}
                </div>
                <div className="form-group inline">
                  <label>
                    <input type="checkbox" name="status" checked={formData.status} onChange={handleChange} />
                    Active
                  </label>
                </div>
              </div>
              <div className="modal-actions">
                <button type="submit">{editingItem ? 'Update' : 'Save'}</button>
                <button type="button" onClick={handleCloseModal} className="secondary">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ItemList;
