import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StoreDashboard.css';

const StoreDashboard = ({ setIsAuthenticated }) => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [storeInfo, setStoreInfo] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    if (setIsAuthenticated) setIsAuthenticated(false);
    navigate('/login');
  };

  useEffect(() => {
    const fetchStoreForUser = async () => {
      if (user?.role !== 'STORE_USER' || !user?.userName) return;
      try {
        setLoading(true);
        const res = await fetch(`/api/stores/by-user/${encodeURIComponent(user.userName)}`);
        const data = await res.json();
        if (data.success) {
          setStoreInfo(Array.isArray(data.stores) ? data.stores : []);
        } else {
          setError(data.message || 'Failed to load store info');
        }
      } catch (e) {
        setError('Network error while loading store info');
      } finally {
        setLoading(false);
      }
    };
    fetchStoreForUser();
  }, [user?.role, user?.userName]);

  return (
    <div className="store-dashboard-container">
      <header className="store-dashboard-header">
        <div className="header-content">
          <h1>Store Dashboard</h1>
          {user.role === 'STORE_USER' && (
            <div className="store-subtitle">
              {loading ? 'Loading store...' : (
                storeInfo.length > 0 ? `Store: ${storeInfo[0].storeCode} - ${storeInfo[0].storeName}` : (error || 'No store mapped')
              )}
            </div>
          )}
          <div className="user-info">
            <span>Logged in: {user.userName || 'User'}</span>
            <span className="user-role">{user.role ? `Role: ${user.role}` : ''}</span>
            <button onClick={handleLogout} className="logout-button">Logout</button>
          </div>
        </div>
      </header>

      <main className="store-dashboard-main">
        <div className="menu-grid">
          <div className="menu-card" onClick={() => navigate('/stores')}>
            <h3>Master</h3>
            <p>Manage master data like stores, categories, brands, sizes</p>
            <button className="menu-btn">Go to Masters</button>
          </div>

          <div className="menu-card" onClick={() => navigate('/transactions')}> 
            <h3>Transaction</h3>
            <p>Perform store transactions (coming soon)</p>
            <button className="menu-btn">Open Transactions</button>
          </div>

          <div className="menu-card" onClick={() => navigate('/reports')}>
            <h3>Reports</h3>
            <p>View store and sales reports (coming soon)</p>
            <button className="menu-btn">View Reports</button>
          </div>
        </div>

        <div className="back-actions">
          <button className="dashboard-btn" onClick={() => navigate('/dashboard')}>Back to Admin Dashboard</button>
        </div>
      </main>
    </div>
  );
};

export default StoreDashboard;
